import React, { useCallback, useEffect, useRef, useState } from "react";

// Types
interface Resources {
  food: number;
  water: number;
  materials: number;
  survivors: number;
}

interface BaseUpgrade {
  id: string;
  name: string;
  description: string;
  cost: Partial<Resources>;
  effect: string;
  level: number;
  maxLevel: number;
}

interface MapLocation {
  id: string;
  name: string;
  x: number;
  y: number;
  discovered: boolean;
  scavengeTime: number;
  lastScavenged: number | null;
  resources: Partial<Resources>;
  isBase?: boolean; // Optional property to mark the base location
  wasScavenged?: boolean; // Optional property to track if the location was already scavenged
  owner?: string | null; // New property to track ownership
}

interface Building {
  id: string;
  type: "house" | "water_collector"; // Define building types
  locationId: string; // The ID of the MapLocation this building is on
  level: number;
  imageUrl?: string; // Optional image URL for visual representation
}

interface Disaster {
  id: string;
  name: string;
  description: string;
  effect: Partial<Resources>;
}

interface AIAttack {
  locationId: string;
  locationName: string;
  success: boolean;
  resourcesLost?: Partial<Resources>;
  survivorsLost?: number;
}

interface GameState {
  resources: Resources;
  upgrades: BaseUpgrade[];
  locations: MapLocation[];
  lastSave: number;
  autoScavengeEnabled: boolean;
}

// Constants
const SAVE_KEY = "apocalypse-idle-save";
const AUTO_SAVE_INTERVAL = 10000; // 10 seconds
const DISASTER_CHANCE = 0.002; // 0.2% chance per second
const AI_ATTACK_CHANCE = 0.001; // 0.1% chance per second for AI to attack
const EXPLORATION_COST = { materials: 20 }; // Cost to explore a new adjacent cell

const initialResources: Resources = {
  food: 100,
  water: 100,
  materials: 50,
  survivors: 5,
};

const disasters: Disaster[] = [
  {
    id: "raiders",
    name: "Raider Attack!",
    description: "Raiders have attacked your base!",
    effect: { food: -20, water: -15, materials: -10 },
  },
  {
    id: "drought",
    name: "Drought",
    description: "Water supplies are running low!",
    effect: { water: -30 },
  },
  {
    id: "sickness",
    name: "Disease Outbreak",
    description: "A disease has spread through your camp!",
    effect: { survivors: -1, food: -10 },
  },
];

const ApocalypticGame: React.FC = () => {
  // State
  const [resources, setResources] = useState<Resources>(initialResources);
  const [activeTab, setActiveTab] = useState<"resources" | "map">("resources");
  const [isMobile, setIsMobile] = useState(false);
  const [showRadialMenu, setShowRadialMenu] = useState(false);
  const [radialMenuPosition, setRadialMenuPosition] = useState({ x: 0, y: 0 });
  const [selectedLocation, setSelectedLocation] = useState<MapLocation | null>(
    null,
  );
  const [showDisaster, setShowDisaster] = useState<Disaster | null>(null);
  const [autoScavengeEnabled, setAutoScavengeEnabled] = useState(true);

  const [scavengeResult, setScavengeResult] =
    useState<Partial<Resources> | null>(null);
  const [showAttackResult, setShowAttackResult] = useState<{ success: boolean; locationName: string; resourcesGained?: Partial<Resources>; survivorsLost?: number } | null>(null);
  const [showAIAttack, setShowAIAttack] = useState<AIAttack | null>(null); // New state for AI attack results

  const [waitingForScavengePopup, setWaitingForScavengePopup] = useState(false);
  const [showTakeoverAnimation, setShowTakeoverAnimation] = useState<string | null>(null);

  const gameLoopRef = useRef<ReturnType<typeof setInterval> | null>(null);
  const autoSaveRef = useRef<ReturnType<typeof setInterval> | null>(null);

  const [upgrades, setUpgrades] = useState<BaseUpgrade[]>([
    {
      id: "farm",
      name: "Makeshift Farm",
      description: "Produces food over time",
      cost: { materials: 50 },
      effect: "+2 food/minute",
      level: 0,
      maxLevel: 5,
    },
    {
      id: "well",
      name: "Water Collector",
      description: "Collects water from the atmosphere",
      cost: { materials: 40 },
      effect: "+1 water/minute",
      level: 0,
      maxLevel: 5,
    },
    {
      id: "shelter",
      name: "Survivor Shelter",
      description: "Attracts new survivors",
      cost: { materials: 100, food: 50 },
      effect: "+1 survivor chance/hour",
      level: 0,
      maxLevel: 3,
    },
  ]);

  const GRID_SIZE = 5;

  const locationNames = [
    "Dustfield",
    "Ashrock",
    "Ironpit",
    "Deadwell",
    "Cragpoint",
    "Burnpass",
    "Mudspire",
    "Wretchend",
    "Redhollow",
    "Saltmoor",
    "Scraptown",
    "Charcliff",
    "Dryreach",
    "Ragbank",
    "Blightden",
    "Boneplain",
    "Rustbarrow",
    "Smokefall",
    "Shivglen",
    "Flintgorge",
    "Gravefen",
    "Scorchbay",
    "Fogscar",
    "Ruinstep",
    "Crimsonlot",
  ];

  const generateInitialGrid = (): MapLocation[] => {
    const locations: MapLocation[] = [];

    for (let y = 0; y < GRID_SIZE; y++) {
      for (let x = 0; x < GRID_SIZE; x++) {
        const isBase =
          x === Math.floor(GRID_SIZE / 2) && y === Math.floor(GRID_SIZE / 2);

        locations.push({
          id: `cell-${x}-${y}`,
          name: isBase
            ? "Your Base"
            : locationNames[y * GRID_SIZE + x] || `Sector-${x}-${y}`,
          isBase: isBase,
          x,
          y,
          discovered: isBase,
          scavengeTime: isBase ? 0 : 5000 + Math.floor(Math.random() * 5000),
          lastScavenged: null,
          resources: isBase
            ? {}
            : Math.random() < 0.6 // change of having resoureces on newly discovered locations
            ? {
                food: Math.floor(Math.random() * 30),
                water: Math.floor(Math.random() * 20),
                materials:
                  Math.random() < 0.5 ? Math.floor(Math.random() * 40) : 0,
                survivors: Math.random() < 0.2 ? 1 : 0,
              }
            : {},
          owner: isBase ? "player" : null, // Set owner for base and null for others
        });
      }
    }

    return locations;
  };

  const [locations, setLocations] = useState<MapLocation[]>(
    generateInitialGrid(),
  );

  const availableBuildings: Building[] = [
    { id: "house-blueprint", type: "house", name: "House", cost: { materials: 30, food: 10 }, imageUrl: "https://i.imgur.com/your-house-image.png" },
    // { id: "water-collector-blueprint", type: "water_collector", name: "Water Collector", cost: { materials: 40 }, imageUrl: "https://i.imgur.com/your-water-collector-image.png" },
  ];

  const resourcesRef = useRef(resources);
  const upgradesRef = useRef(upgrades);

  // Update refs whenever state changes
  useEffect(() => {
    resourcesRef.current = resources;
  }, [resources]);
  useEffect(() => {
    upgradesRef.current = upgrades;
  }, [upgrades]);

  // Responsive design
  useEffect(() => {
    const checkMobile = () => {
      setIsMobile(window.innerWidth < 768);
    };
    checkMobile();
    window.addEventListener("resize", checkMobile);
    return () => window.removeEventListener("resize", checkMobile);
  }, []);

  // Load game
  useEffect(() => {
    const savedGame = localStorage.getItem(SAVE_KEY);
    if (savedGame) {
      try {
        const gameState: GameState = JSON.parse(savedGame);
        const timePassed = Date.now() - gameState.lastSave;

        // Calculate offline progression
        const minutesPassed = timePassed / 60000;
        const offlineResources = { ...gameState.resources };

        gameState.upgrades.forEach((upgrade) => {
          if (upgrade.id === "farm" && upgrade.level > 0) {
            offlineResources.food += Math.floor(
              minutesPassed * 2 * upgrade.level * 0.5,
            ); // 50% efficiency offline
          }
          if (upgrade.id === "well" && upgrade.level > 0) {
            offlineResources.water += Math.floor(
              minutesPassed * 1 * upgrade.level * 0.5,
            );
          }
          if (upgrade.id === "shelter" && upgrade.level > 0) {
            offlineResources.survivors += Math.floor(
              minutesPassed * (1 / 60) * upgrade.level * 0.5,
            );
          }
        });

        setResources(offlineResources);
        setUpgrades(gameState.upgrades);
        setLocations(gameState.locations);
        setAutoScavengeEnabled(gameState.autoScavengeEnabled);
      } catch (e) {
        console.error("Failed to load save:", e);
      }
    }
  }, []);

  // Save game
  const saveGame = useCallback(() => {
    const gameState: GameState = {
      resources,
      upgrades,
      locations,
      lastSave: Date.now(),
      autoScavengeEnabled,
    };
    localStorage.setItem(SAVE_KEY, JSON.stringify(gameState));
  }, [resources, upgrades, locations, autoScavengeEnabled]);

  // Auto save
  useEffect(() => {
    autoSaveRef.current = setInterval(saveGame, AUTO_SAVE_INTERVAL);
    return () => {
      if (autoSaveRef.current) clearInterval(autoSaveRef.current);
    };
  }, [saveGame]);

  function floorResources(res: Resources): Resources {
    return {
      food: Math.floor(res.food),
      water: Math.floor(res.water),
      materials: Math.floor(res.materials),
      survivors: Math.floor(res.survivors),
    };
  }

  function isEqualResources(a: Resources, b: Resources): boolean {
    return (
      a.food === b.food &&
      a.water === b.water &&
      a.materials === b.materials &&
      a.survivors === b.survivors
    );
  }

  // Game loop
  useEffect(() => {
    gameLoopRef.current = setInterval(() => {
      setResources((prev) => {
        const newResources = { ...prev };

        // Apply upgrade effects
        upgrades.forEach((upgrade) => {
          if (upgrade.id === "farm" && upgrade.level > 0) {
            newResources.food += (2 * upgrade.level) / 60; // per second
          }
          if (upgrade.id === "well" && upgrade.level > 0) {
            newResources.water += (1 * upgrade.level) / 60;
          }
          if (upgrade.id === "shelter" && upgrade.level > 0) {
            newResources.survivors += (1 * upgrade.level) / 3600; // 1 per hour
          }
        });

        // Survivor consumption
        newResources.food = Math.max(
          0,
          newResources.food - prev.survivors * 0.01,
        );
        newResources.water = Math.max(
          0,
          newResources.water - prev.survivors * 0.02,
        );

        // Floor and compare
        const floored = floorResources(newResources);
        if (!isEqualResources(floored, floorResources(prev))) {
          return floored;
        }
        return prev; // Skip update if unchanged
      });

      // Check for disasters
      if (Math.random() < DISASTER_CHANCE) {
        const disaster =
          disasters[Math.floor(Math.random() * disasters.length)];
        setShowDisaster(disaster);
        applyDisaster(disaster);
      }

      // Check for AI attacks
      if (Math.random() < AI_ATTACK_CHANCE) {
        triggerAIAttack();
      }

      // Auto scavenging
      if (autoScavengeEnabled) {
        checkAutoScavenge();
      }
    }, 2000);

    return () => {
      if (gameLoopRef.current) clearInterval(gameLoopRef.current);
    };
  }, [upgrades, autoScavengeEnabled]);

  useEffect(() => {
    const interval = setInterval(() => {
      const now = Date.now();
      if (waitingForScavengePopup || scavengeResult) return;

      const finished = locations.find(
        (loc) =>
          loc.lastScavenged &&
          loc.scavengeTime > 0 &&
          now - loc.lastScavenged >= loc.scavengeTime,
      );

      if (finished) finalizeScavenge(finished);
    }, 1000);

    return () => clearInterval(interval);
  }, [locations, waitingForScavengePopup, scavengeResult]);

  const applyDisaster = (disaster: Disaster) => {
    setResources((prev) => ({
      food: Math.max(0, prev.food + (disaster.effect.food || 0)),
      water: Math.max(0, prev.water + (disaster.effect.water || 0)),
      materials: Math.max(0, prev.materials + (disaster.effect.materials || 0)),
      survivors: Math.max(1, prev.survivors + (disaster.effect.survivors || 0)),
    }));
  };

  const checkAutoScavenge = () => {
    if (waitingForScavengePopup) return;

    const now = Date.now();

    setLocations((prev) => {
      let hasActiveScavenge = false;
      let updated = false;

      const newLocations = prev.map((loc) => {
        if (loc.lastScavenged && loc.scavengeTime > 0) {
          if (now - loc.lastScavenged >= loc.scavengeTime && !updated) {
            const loot = {
              food: loc.resources.food || 0,
              water: loc.resources.water || 0,
              materials: loc.resources.materials || 0,
              survivors: loc.resources.survivors || 0,
            };

            finalizeScavenge(loc);
            updated = true;
            return loc;
          }

          hasActiveScavenge = true;
          return loc;
        }

        return loc;
      });

      if (!hasActiveScavenge && !updated) {
        // Start auto-scavenge on the first available location
        const targetIndex = newLocations.findIndex(
          (loc) =>
            loc.discovered &&
            !loc.lastScavenged &&
            loc.scavengeTime > 0 &&
            Object.values(loc.resources).some((v) => v && v > 0),
        );

        if (targetIndex !== -1) {
          newLocations[targetIndex] = {
            ...newLocations[targetIndex],
            lastScavenged: now,
          };
          updated = true;
        }
      }

      return newLocations;
    });
  };

  const finalizeScavenge = (location: MapLocation) => {
    const loot = {
      food: location.resources.food || 0,
      water: location.resources.water || 0,
      materials: location.resources.materials || 0,
      survivors: location.resources.survivors || 0,
    };

    setScavengeResult(loot);
    setResources((prevResources) => ({
      food: prevResources.food + loot.food,
      water: prevResources.water + loot.water,
      materials: prevResources.materials + loot.materials,
      survivors: prevResources.survivors + loot.survivors,
    }));
    setWaitingForScavengePopup(true);

    setLocations((prev) =>
      prev.map((loc) =>
        loc.id === location.id
          ? { ...loc, lastScavenged: null, resources: {}, wasScavenged: true }
          : loc,
      ),
    );
  };

  const handleMapClick = (
    e: React.MouseEvent<HTMLDivElement>,
    location: MapLocation,
  ) => {
    e.stopPropagation();
    if (!location.discovered && location.id !== "base") return;

    setSelectedLocation(location);
    setRadialMenuPosition({ x: e.clientX, y: e.clientY });
    setShowRadialMenu(true);
  };

  const scavengeLocation = (location: MapLocation) => {
    if (location.lastScavenged || location.scavengeTime === 0) return;

    setLocations((prev) =>
      prev.map((loc) =>
        loc.id === location.id ? { ...loc, lastScavenged: Date.now() } : loc,
      ),
    );
    setShowRadialMenu(false);
  };

  const exploreNearby = () => {
    if (!selectedLocation) return;

    const cost = EXPLORATION_COST;
    const canAfford = Object.entries(cost).every(
      ([resource, amount]) => resources[resource as keyof Resources] >= (amount || 0)
    );

    if (!canAfford) {
      console.log("Not enough materials to explore!");
      setShowRadialMenu(false);
      return;
    }

    // Deduct cost
    setResources(prev => {
      const newResources = { ...prev };
      Object.entries(cost).forEach(([resource, amount]) => {
        newResources[resource as keyof Resources] -= amount || 0;
      });
      return newResources;
    });

    const getGridCoords = (id: string) => {
      const match = id.match(/cell-(\d+)-(\d+)/);
      if (!match) return null;
      return { x: parseInt(match[1]), y: parseInt(match[2]) };
    };

    const selectedCoords = getGridCoords(selectedLocation.id);
    if (!selectedCoords) return;

    setLocations((prev) =>
      prev.map((loc) => {
        const coords = getGridCoords(loc.id);
        if (!coords || loc.discovered) return loc;

        const dx = Math.abs(coords.x - selectedCoords.x);
        const dy = Math.abs(coords.y - selectedCoords.y);

        if (dx <= 1 && dy <= 1) {
          return { ...loc, discovered: true };
        }

        return loc;
      }),
    );

    setShowRadialMenu(false);
  };

  const purchaseUpgrade = (upgrade: BaseUpgrade) => {
    if (upgrade.level >= upgrade.maxLevel) return;

    const canAfford = Object.entries(upgrade.cost).every(
      ([resource, cost]) =>
        resources[resource as keyof Resources] >= (cost || 0),
    );

    if (canAfford) {
      setResources((prev) => {
        const newResources = { ...prev };
        Object.entries(upgrade.cost).forEach(([resource, cost]) => {
          newResources[resource as keyof Resources] -= cost || 0;
        });
        return newResources;
      });

      setUpgrades((prev) =>
        prev.map((u) =>
          u.id === upgrade.id
            ? {
                ...u,
                level: u.level + 1,
                cost: Object.fromEntries(
                  Object.entries(u.cost).map(([r, c]) => [
                    r,
                    Math.floor((c || 0) * 1.5),
                  ]),
                ),
              }
            : u,
        ),
      );
    }
  };

  const getScavengeProgress = (location: MapLocation) => {
    if (!location.lastScavenged || location.scavengeTime === 0) return 0;
    const elapsed = Date.now() - location.lastScavenged;
    return Math.min(100, (elapsed / location.scavengeTime) * 100);
  };

  const attackLocation = (location: MapLocation) => {
    const success = Math.random() < 0.5; // 50% chance of success

    if (success) {
      const materialsGained = 20 + Math.floor(Math.random() * 30);
      setResources((prev) => ({
        ...prev,
        materials: prev.materials + materialsGained,
      }));
      setLocations((prev) =>
        prev.map((loc) =>
          loc.id === location.id ? { ...loc, owner: "player" } : loc,
        ),
      );
      setShowAttackResult({
        success: true,
        locationName: location.name,
        resourcesGained: { materials: materialsGained },
      });
      setShowTakeoverAnimation(location.id); // Trigger takeover animation
    } else {
      const survivorsLost = 1; // Always lose 1 survivor on failure
      setResources((prev) => ({
        ...prev,
        survivors: Math.max(0, prev.survivors - survivorsLost),
      }));
      setShowAttackResult({
        success: false,
        locationName: location.name,
        survivorsLost: survivorsLost,
      });
    }
    setShowRadialMenu(false);
  };

  const buyBackLocation = (location: MapLocation) => {
    const cost = { materials: 50 }; // Example cost

    const canAfford = Object.entries(cost).every(
      ([resource, amount]) => resources[resource as keyof Resources] >= (amount || 0)
    );

    if (canAfford) {
      setResources(prev => {
        const newResources = { ...prev };
        Object.entries(cost).forEach(([resource, amount]) => {
          newResources[resource as keyof Resources] -= amount || 0;
        });
        return newResources;
      });

      setLocations(prev =>
        prev.map(loc =>
          loc.id === location.id ? { ...loc, owner: "player" } : loc
