                );
                return (
                  <div
                    key={building.id}
                    draggable
                    onDragStart={(e) => onDragStart(e, building.id)}
                    className={`bg-gray-800 bg-opacity-60 backdrop-filter backdrop-blur-sm rounded-xl p-4 shadow-lg flex flex-col items-center text-center border border-gray-700 transition-all duration-300
                      ${canAffordBuilding ? "cursor-grab active:cursor-grabbing hover:shadow-xl" : "opacity-50 cursor-not-allowed"}
                    `}
