  const MapView = () => (
    <div className="relative bg-gray-900 border border-gray-700 rounded-xl p-8 shadow-2xl h-full flex flex-col overflow-hidden before:absolute before:inset-0 before:bg-gradient-to-br before:from-gray-800 before:via-gray-900 before:to-black before:opacity-50 before:rounded-xl">
      <h2 className="relative z-10 text-4xl font-extrabold text-white mb-8 text-center drop-shadow-md">
