              className={`relative flex items-center justify-center p-1 transition-all duration-300 group ${location.discovered ? "cursor-pointer" : "opacity-30"} ${isAnimatingTakeover ? 'animate-pulse-green' : ''}`}
              onClick={(e) => handleMapClick(e, location)}
            >
