                  >
                    {location.isBase
                      ? "🏠"
                      : location.owner === "player"
                      ? "✅" // Owned by player, show checkmark or similar
                      : location.resources &&
                        Object.values(location.resources).some(
                          (val) => val && val > 0,
                        )
                      ? "💰"
                      : ""}
                  </div>
                  <div className="absolute bottom-0 left-0 right-0 bg-gray-800 bg-opacity-80 p-1 text-center backdrop-blur-sm group-hover:bg-opacity-90 transition-opacity">
                    <p className="text-xs font-semibold text-gray-200 truncate">
                      {location.name}
                    </p>
                  </div>
                  {location.lastScavenged && location.scavengeTime > 0 && (
                    <div className="absolute top-0 left-0 right-0 h-1 bg-gray-700 rounded-t">
                      <div
                        className="h-full bg-yellow-500 rounded-t transition-all duration-1000"
                        style={{ width: `${getScavengeProgress(location)}%` }}
                      />
                    </div>
                  )}
                </div>
              ) : (
                <div className="w-full h-full rounded-md bg-gray-800 flex items-center justify-center text-4xl opacity-50 animate-pulse border border-gray-700"></div>
              )}
