  useEffect(() => {
    if (showTakeoverAnimation) {
      const timer = setTimeout(() => {
        setShowTakeoverAnimation(null);
      }, 1500); // Animation duration
      return () => clearTimeout(timer);
    }
  }, [showTakeoverAnimation]);

  // Components
  const RadialMenu = () => {
    if (!showRadialMenu || !selectedLocation) return null;

    const actions = [
      {
        label: "Scavenge",
        icon: "🔍",
        enabled:
          !selectedLocation.lastScavenged &&
          selectedLocation.scavengeTime > 0 &&
          Object.values(selectedLocation.resources).some(
            (val) => val && val > 0,
          ),
        action: () => scavengeLocation(selectedLocation),
      },
      {
        label: "Attack",
        icon: "⚔️",
        enabled: 
          selectedLocation.discovered && 
          !selectedLocation.isBase &&
          selectedLocation.owner !== "player",
        action: () => attackLocation(selectedLocation),
      },
      {
        label: "Buy Back",
