      <div className="fixed inset-0 bg-black bg-opacity-70 flex items-center justify-center z-50 p-4">
        <div className={`relative bg-gray-900 border ${isSuccess ? "border-green-700" : "border-red-700"} rounded-xl p-8 max-w-sm shadow-2xl text-center transform scale-95 opacity-0 animate-fade-in-scale`}>
          <h3 className={`text-3xl font-bold ${isSuccess ? "text-green-500" : "text-red-500"} mb-4 drop-shadow-md`}>
            {isSuccess ? "Attack Successful!" : "Attack Failed!"}
          </h3>
          <p className="text-gray-200 text-lg mb-6">
            {isSuccess ? (
              <>
                You successfully took over the <span className="font-semibold">{showAttackResult.locationName}</span>! You gained:
              </>
            ) : (
              <>
                Your attack on <span className="font-semibold">{showAttackResult.locationName}</span> failed! You lost:
              </>
            )}
          </p>
          <ul className="text-gray-200 text-lg space-y-2 mb-6">
            {isSuccess && showAttackResult.resourcesGained ? (
              Object.entries(showAttackResult.resourcesGained).map(([key, value]) =>
                value && value > 0 ? (
                  <li key={key} className="flex items-center justify-center">
                    <span className="capitalize mr-2">{key}:</span>
                    <span className="font-semibold text-white text-2xl">{value}</span>
                  </li>
                ) : null,
              )
            ) : null}
            {!isSuccess && showAttackResult.survivorsLost ? (
              <li className="flex items-center justify-center">
                <span className="capitalize mr-2">Survivors:</span>
                <span className="font-semibold text-white text-2xl">{showAttackResult.survivorsLost}</span>
              </li>
            ) : null}
          </ul>
          <button
            onClick={() => setShowAttackResult(null)}
            className={`w-full px-6 py-3 rounded-lg text-lg font-bold uppercase tracking-wider transition-all duration-300 transform hover:scale-105 shadow-md ${isSuccess
              ? "bg-gradient-to-r from-green-600 to-green-800 hover:from-green-700 hover:to-green-900 text-white"
              : "bg-gradient-to-r from-red-600 to-red-800 hover:from-red-700 hover:to-red-900 text-white"}
            `}
          >
            Continue
          </button>
        </div>
      </div>
    );
  };

  const AIAttackPopup = () => {
    if (!showAIAttack) return null;

    const isSuccess = showAIAttack.success;

    return (
      <div className="fixed inset-0 bg-black bg-opacity-70 flex items-center justify-center z-50 p-4">
        <div className={`relative bg-gray-900 border ${isSuccess ? "border-green-700" : "border-red-700"} rounded-xl p-8 max-w-sm shadow-2xl text-center transform scale-95 opacity-0 animate-fade-in-scale`}>
          <h3 className={`text-3xl font-bold ${isSuccess ? "text-green-500" : "text-red-500"} mb-4 drop-shadow-md`}>
            {isSuccess ? "AI Attack Successful!" : "AI Attack Failed!"}
          </h3>
          <p className="text-gray-200 text-lg mb-6">
            {isSuccess ? (
              <>
                AI successfully took over the <span className="font-semibold">{showAIAttack.locationName}</span>! You lost:
              </>
            ) : (
              <>
                AI attack on <span className="font-semibold">{showAIAttack.locationName}</span> failed! You lost:
              </>
            )}
          </p>
          <ul className="text-gray-200 text-lg space-y-2 mb-6">
            {isSuccess && showAIAttack.survivorsLost ? (
              <li className="flex items-center justify-center">
                <span className="capitalize mr-2">Survivors:</span>
                <span className="font-semibold text-white text-2xl">{showAIAttack.survivorsLost}</span>
              </li>
            ) : null}
          </ul>
          <button
            onClick={() => setShowAIAttack(null)}
            className={`w-full px-6 py-3 rounded-lg text-lg font-bold uppercase tracking-wider transition-all duration-300 transform hover:scale-105 shadow-md ${isSuccess
              ? "bg-gradient-to-r from-green-600 to-green-800 hover:from-green-700 hover:to-green-900 text-white"
              : "bg-gradient-to-r from-red-600 to-red-800 hover:from-red-700 hover:to-red-900 text-white"}
            `}
          >
            Continue
          </button>
        </div>
      </div>
    );
  };

  return (
    <div className="min-h-screen bg-gray-950 text-white font-sans antialiased relative overflow-hidden">
      {/* Background overlay for subtle texture/effect */}
      <div className="absolute inset-0 z-0 opacity-20" style={{ backgroundImage: `url('https://www.transparenttextures.com/patterns/dust-and-stones.png')`, backgroundSize: 'cover' }}></div>

      {isMobile ? (
        // Mobile layout with tabs
        <div className="relative z-10 px-4 py-6 h-screen flex flex-col">
          <h1 className="text-5xl font-extrabold text-center mb-8 text-white drop-shadow-lg leading-tight">
            Wasteland
            <br />
            Survivor
          </h1>
          <div className="flex mb-6 bg-gray-800 rounded-xl p-1 shadow-inner border border-gray-700">
            <button
              onClick={() => setActiveTab("resources")}
              className={`flex-1 py-3 px-4 rounded-lg text-lg font-bold transition-all duration-300 transform hover:scale-105 shadow-md ${
                activeTab === "resources"
                  ? "bg-gradient-to-r from-blue-600 to-blue-800 text-white" : "text-gray-400 hover:text-white hover:bg-gray-700"
              }`}
            >
              Resources
            </button>
            <button
              onClick={() => setActiveTab("map")}
              className={`flex-1 py-3 px-4 rounded-lg text-lg font-bold transition-all duration-300 transform hover:scale-105 shadow-md ${
                activeTab === "map"
                  ? "bg-gradient-to-r from-blue-600 to-blue-800 text-white" : "text-gray-400 hover:text-white hover:bg-gray-700"
              }`}
            >
              Map
            </button>
          </div>
          {activeTab === "resources" ? (
            <ResourcePanel
              resources={resources}
              upgrades={upgrades}
              autoScavengeEnabled={autoScavengeEnabled}
              purchaseUpgrade={purchaseUpgrade}
              setAutoScavengeEnabled={setAutoScavengeEnabled}
              availableBuildings={availableBuildings}
            />
          ) : (
            <MapView />
          )}
        </div>
      ) : (
        // Desktop split-screen layout
        <div className="relative z-10 flex h-screen p-8 gap-8">
          <div className="w-1/2 flex flex-col">
            <h1 className="text-5xl font-extrabold mb-8 text-white drop-shadow-lg leading-tight text-center">
              Wasteland
              <br />
              Survivor
            </h1>
            <ResourcePanel
              resources={resources}
              upgrades={upgrades}
              autoScavengeEnabled={autoScavengeEnabled}
              purchaseUpgrade={purchaseUpgrade}
              setAutoScavengeEnabled={setAutoScavengeEnabled}
              availableBuildings={availableBuildings}
            />
          </div>
          <div className="w-1/2 flex flex-col h-full">
            <MapView />
          </div>
        </div>
      )}

      <RadialMenu />
      <DisasterPopup />
      <ScavengePopup />
      <AttackResultPopup />
      <AIAttackPopup /> {/* New component for AI attack results */}
    </div>
  );
};

export default ApocalypticGame;
