import React, { useCallback, useEffect, useRef, useState } from "react";

// Types
interface Resources {
  food: number;
  water: number;
  materials: number;
  survivors: number;
}

interface BaseUpgrade {
  id: string;
  name: string;
  description: string;
  cost: Partial<Resources>;
  effect: string;
  level: number;
  maxLevel: number;
}

interface MapLocation {
  id: string;
  name: string;
  x: number;
  y: number;
  discovered: boolean;
  scavengeTime: number;
  lastScavenged: number | null;
  resources: Partial<Resources>;
  isBase?: boolean; // Optional property to mark the base location
  wasScavenged?: boolean; // Optional property to track if the location was already scavenged
  owner?: string | null; // New property to track ownership
}

interface Building {
  id: string;
  type: "house" | "water_collector"; // Define building types
  locationId?: string; // The ID of the MapLocation this building is on (optional, for blueprints)
  level: number;
  imageUrl?: string; // Optional image URL for visual representation
  name: string; // Added name property
  cost: Partial<Resources>; // Added cost property
}

interface Disaster {
  id: string;
  name: string;
  description: string;
  effect: Partial<Resources>;
}

interface AIAttack {
  locationId: string;
  locationName: string;
  success: boolean;
  resourcesLost?: Partial<Resources>;
  survivorsLost?: number;
}

interface RadialMenuAction {
  label: string;
  icon: JSX.Element | string;
  enabled: boolean;
  action: () => void;
  cost?: Partial<Resources>;
}

interface GameState {
  resources: Resources;
  upgrades: BaseUpgrade[];
  locations: MapLocation[];
  lastSave: number;
  autoScavengeEnabled: boolean;
}

// Constants
const SAVE_KEY = "apocalypse-idle-save";
const AUTO_SAVE_INTERVAL = 10000; // 10 secon
const DISASTER_CHANCE = 0.002;
const AI_ATTACK_CHANCE = 0.02; 
const HOUSE_CAPACITY = 2;
const UNHOUSED_SURVIVOR_DEATH_RATE = 0.01;
const EXPLORATION_COST = { 
  materials: 20,
};

const initialResources: Resources = {
  food: 100,
  water: 100,
  materials: 50,
  survivors: 5,
};

const disasters: Disaster[] = [
  {
    id: "raiders",
    name: "Raider Attack!",
    description: "Raiders have attacked your base!",
    effect: { food: -20, water: -15, materials: -10 },
  },
  {
    id: "drought",
    name: "Drought",
    description: "Water supplies are running low!",
    effect: { water: -30 },
  },
  {
    id: "sickness",
    name: "Disease Outbreak",
    description: "A disease has spread through your camp!",
    effect: { survivors: -1, food: -10 },
  },
];

// Update icon components to accept className props
const FoodIcon = ({ className = "h-8 w-8" }: { className?: string }) => (
  <svg xmlns="http://www.w3.org/2000/svg" className={className} viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
    <path d="M18 8h1a4 4 0 0 1 0 8h-1"></path>
    <path d="M2 8h16v9a4 4 0 0 1-4 4H6a4 4 0 0 1-4-4V8z"></path>
    <line x1="6" y1="1" x2="6" y2="4"></line>
    <line x1="10" y1="1" x2="10" y2="4"></line>
    <line x1="14" y1="1" x2="14" y2="4"></line>
  </svg>
);

const WaterIcon = ({ className = "h-8 w-8" }: { className?: string }) => (
  <svg xmlns="http://www.w3.org/2000/svg" className={className} viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
    <path d="M12 2.69l5.66 5.66a8 8 0 1 1-11.31 0z"></path>
  </svg>
);

const MaterialsIcon = ({ className = "h-8 w-8" }: { className?: string }) => (
  <svg xmlns="http://www.w3.org/2000/svg" className={className} viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
    <path d="M14.7 6.3a1 1 0 0 0 0 1.4l1.6 1.6a1 1 0 0 0 1.4 0l3.77-3.77a6 6 0 0 1-7.94 7.94l-6.91 6.91a2.12 2.12 0 0 1-3-3l6.91-6.91a6 6 0 0 1 7.94-7.94l-3.76 3.76z"></path>
  </svg>
);

const SurvivorsIcon = ({ className = "h-8 w-8" }: { className?: string }) => (
  <svg xmlns="http://www.w3.org/2000/svg" className={className} viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
    <path d="M17 21v-2a4 4 0 0 0-4-4H5a4 4 0 0 0-4 4v2"></path>
    <circle cx="9" cy="7" r="4"></circle>
    <path d="M23 21v-2a4 4 0 0 0-3-3.87"></path>
