    <line x1="6" y1="6" x2="18" y2="18"></line>
  </svg>
);

const ApocalypticGame: React.FC = () => {
  // State
  const [resources, setResources] = useState<Resources>(initialResources);
  const [activeTab, setActiveTab] = useState<"resources" | "map">("resources");
  const [isMobile, setIsMobile] = useState(false);
  const [showRadialMenu, setShowRadialMenu] = useState(false);
  const [radialMenuPosition, setRadialMenuPosition] = useState({ x: 0, y: 0 });
  const [selectedLocation, setSelectedLocation] = useState<MapLocation | null>(
    null,
  );
  const [showDisaster, setShowDisaster] = useState<Disaster | null>(null);
  const [autoScavengeEnabled, setAutoScavengeEnabled] = useState(true);
  const [buildings, setBuildings] = useState<Building[]>([]);

  const [scavengeResult, setScavengeResult] =
    useState<Partial<Resources> | null>(null);
  const [showAttackResult, setShowAttackResult] = useState<{ success: boolean; locationName: string; resourcesGained?: Partial<Resources>; survivorsLost?: number } | null>(null);
  const [showAIAttack, setShowAIAttack] = useState<AIAttack | null>(null); // New state for AI attack results
  const [showAttackedAnimation, setShowAttackedAnimation] = useState<string | null>(null); // New state for AI attack animation
  const [showHowToPlay, setShowHowToPlay] = useState(false); // New state for How to Play popup

  const [waitingForScavengePopup, setWaitingForScavengePopup] = useState(false);

  const gameLoopRef = useRef<ReturnType<typeof setInterval> | null>(null);
  const autoSaveRef = useRef<ReturnType<typeof setInterval> | null>(null);

  const [upgrades, setUpgrades] = useState<BaseUpgrade[]>([
    {
      id: "farm",
      name: "Makeshift Farm",
      description: "Produces food over time",
      cost: { materials: 50 },
      effect: "+5 food/minute",
      level: 0,
      maxLevel: 5,
    },
    {
      id: "well",
      name: "Water Collector",
      description: "Collects water from the atmosphere",
      cost: { materials: 40 },
      effect: "+3 water/minute",
      level: 0,
      maxLevel: 5,
    },
    {
      id: "shelter",
      name: "Survivor Shelter",
      description: "Attracts new survivors",
      cost: { materials: 100, food: 50 },
      effect: "+1 survivor chance/hour",
      level: 0,
      maxLevel: 3,
    },
  ]);

  const GRID_SIZE = 5;

  const locationNames = [
    "Dustfield",
    "Ashrock",
    "Ironpit",
    "Deadwell",
    "Cragpoint",
    "Burnpass",
    "Mudspire",
    "Wretchend",
    "Redhollow",
    "Saltmoor",
    "Scraptown",
    "Charcliff",
    "Dryreach",
    "Ragbank",
    "Blightden",
    "Boneplain",
    "Rustbarrow",
    "Smokefall",
    "Shivglen",
    "Flintgorge",
    "Gravefen",
    "Scorchbay",
    "Fogscar",
    "Ruinstep",
    "Crimsonlot",
  ];

  const generateInitialGrid = (): MapLocation[] => {
    const locations: MapLocation[] = [];

    for (let y = 0; y < GRID_SIZE; y++) {
      for (let x = 0; x < GRID_SIZE; x++) {
        const isBase =
          x === Math.floor(GRID_SIZE / 2) && y === Math.floor(GRID_SIZE / 2);

        locations.push({
          id: `cell-${x}-${y}`,
          name: isBase
            ? "Your Base"
            : locationNames[y * GRID_SIZE + x] || `Sector-${x}-${y}`,
          isBase: isBase,
          x,
          y,
          discovered: isBase,
          scavengeTime: isBase ? 0 : 5000 + Math.floor(Math.random() * 5000),
          lastScavenged: null,
          resources: isBase
            ? {}
            : Math.random() < 0.8 // Increased chance of having resources on newly discovered locations
            ? {
                food: Math.floor(Math.random() * 50) + 10, // Increased food range
                water: Math.floor(Math.random() * 30) + 5, // Increased water range
                materials:
                  Math.random() < 0.7 ? Math.floor(Math.random() * 60) + 10 : 0, // Increased material chance and range
                survivors: Math.random() < 0.3 ? 1 : 0, // Slightly increased survivor chance
              }
            : {},
          owner: isBase ? "player" : null, // Set owner for base and null for others
        });
      }
    }

    return locations;
  };

  const [locations, setLocations] = useState<MapLocation[]>(
    generateInitialGrid(),
  );

  const availableBuildings: Building[] = [
    { id: "house-blueprint", type: "house", name: "House", cost: { materials: 30, food: 10 }, level: 0, imageUrl: "https://img.icons8.com/ios-filled/50/ffffff/home.png" },
    // { id: "water-collector-blueprint", type: "water_collector", name: "Water Collector", cost: { materials: 40 }, imageUrl: "https://i.imgur.com/your-water-collector-image.png" },
  ];

  const resourcesRef = useRef(resources);
  const upgradesRef = useRef(upgrades);

  // Update refs whenever state changes
  useEffect(() => {
    resourcesRef.current = resources;
  }, [resources]);
  useEffect(() => {
    upgradesRef.current = upgrades;
  }, [upgrades]);

  // Responsive design
  useEffect(() => {
    const checkMobile = () => {
      setIsMobile(window.innerWidth < 768);
    };
    checkMobile();
    window.addEventListener("resize", checkMobile);
    return () => window.removeEventListener("resize", checkMobile);
  }, []);

  // Load game
  useEffect(() => {
    const savedGame = localStorage.getItem(SAVE_KEY);
    if (savedGame) {
      try {
        const gameState: GameState = JSON.parse(savedGame);
        const timePassed = Date.now() - gameState.lastSave;

        // Calculate offline progression
        const minutesPassed = timePassed / 60000;
        const offlineResources = { ...gameState.resources };

        gameState.upgrades.forEach((upgrade) => {
          if (upgrade.id === "farm" && upgrade.level > 0) {
            offlineResources.food += Math.floor(
              minutesPassed * 5 * upgrade.level * 0.5,
            ); // 50% efficiency offline
          }
          if (upgrade.id === "well" && upgrade.level > 0) {
            offlineResources.water += Math.floor(
              minutesPassed * 3 * upgrade.level * 0.5,
            );
          }
          if (upgrade.id === "shelter" && upgrade.level > 0) {
            offlineResources.survivors += Math.floor(
              minutesPassed * (1 / 60) * upgrade.level * 0.5,
            );
          }
        });

        setResources(offlineResources);
        setUpgrades(gameState.upgrades);
        setLocations(gameState.locations);
        setAutoScavengeEnabled(gameState.autoScavengeEnabled);
      } catch (e) {
        console.error("Failed to load save:", e);
      }
    }
  }, []);

  // Save game
  const saveGame = useCallback(() => {
    const gameState: GameState = {
      resources,
      upgrades,
      locations,
      lastSave: Date.now(),
      autoScavengeEnabled,
    };
    localStorage.setItem(SAVE_KEY, JSON.stringify(gameState));
  }, [resources, upgrades, locations, autoScavengeEnabled]);

  // Auto save
  useEffect(() => {
    autoSaveRef.current = setInterval(saveGame, AUTO_SAVE_INTERVAL);
    return () => {
      if (autoSaveRef.current) clearInterval(autoSaveRef.current);
    };
  }, [saveGame]);

  function floorResources(res: Resources): Resources {
    return {
      food: Math.floor(res.food),
      water: Math.floor(res.water),
      materials: Math.floor(res.materials),
      survivors: Math.floor(res.survivors),
    };
  }

  function isEqualResources(a: Resources, b: Resources): boolean {
    return (
      a.food === b.food &&
      a.water === b.water &&
      a.materials === b.materials &&
      a.survivors === b.survivors
    );
  }

  // Game loop
  useEffect(() => {
    gameLoopRef.current = setInterval(() => {
      setResources((prev) => {
        const newResources = { ...prev };

        // Apply upgrade effects
        upgrades.forEach((upgrade) => {
          if (upgrade.id === "farm" && upgrade.level > 0) {
            newResources.food += (5 * upgrade.level) / 60; // per second
          }
          if (upgrade.id === "well" && upgrade.level > 0) {
            newResources.water += (3 * upgrade.level) / 60;
          }
          if (upgrade.id === "shelter" && upgrade.level > 0) {
            newResources.survivors += (1 * upgrade.level) / 3600; // 1 per hour
          }
        });

        // Housing check: survivors freeze and die if not enough houses
        const totalHousingCapacity = buildings.filter(b => b.type === "house").length * HOUSE_CAPACITY;
        const unhousedSurvivors = newResources.survivors - totalHousingCapacity;

        if (unhousedSurvivors > 0 && Math.random() < UNHOUSED_SURVIVOR_DEATH_RATE * unhousedSurvivors) {
          const lostSurvivors = Math.min(Math.ceil(unhousedSurvivors / 5), newResources.survivors); // Lose a fraction of unhoused survivors or at least 1
          newResources.survivors = Math.max(0, newResources.survivors - lostSurvivors);
          setShowDisaster({
            id: "exposure",
            name: "Exposure!",
            description: `Some of your unhoused survivors froze to death! Lost ${lostSurvivors} survivor(s).`, // Dynamic message
            effect: { survivors: -lostSurvivors },
          });
        }

        // Survivor consumption
        newResources.food = Math.max(
          0,
          newResources.food - prev.survivors * 0.01,
        );
        newResources.water = Math.max(
          0,
          newResources.water - prev.survivors * 0.02,
        );

        // Floor and compare
        const floored = floorResources(newResources);
        if (!isEqualResources(floored, floorResources(prev))) {
          return floored;
        }
        return prev; // Skip update if unchanged
      });

      // Check for disasters
      if (Math.random() < DISASTER_CHANCE) {
        const disaster =
          disasters[Math.floor(Math.random() * disasters.length)];
        setShowDisaster(disaster);
        applyDisaster(disaster);
      }

      // Check for AI attacks
      if (Math.random() < AI_ATTACK_CHANCE) {
        triggerAIAttack();
      }

      // Auto scavenging
      if (autoScavengeEnabled) {
        checkAutoScavenge();
      }
    }, 2000);

    return () => {
      if (gameLoopRef.current) clearInterval(gameLoopRef.current);
    };
  }, [upgrades, autoScavengeEnabled]);

  useEffect(() => {
    const interval = setInterval(() => {
      const now = Date.now();
      if (waitingForScavengePopup || scavengeResult) return;

      const finished = locations.find(
        (loc) =>
          loc.lastScavenged &&
          loc.scavengeTime > 0 &&
          now - loc.lastScavenged >= loc.scavengeTime,
      );

      if (finished) finalizeScavenge(finished);
    }, 1000);

    return () => clearInterval(interval);
  }, [locations, waitingForScavengePopup, scavengeResult]);

  const applyDisaster = (disaster: Disaster) => {
    setResources((prev) => ({
      food: Math.max(0, prev.food + (disaster.effect.food || 0)),
      water: Math.max(0, prev.water + (disaster.effect.water || 0)),
      materials: Math.max(0, prev.materials + (disaster.effect.materials || 0)),
      survivors: Math.max(1, prev.survivors + (disaster.effect.survivors || 0)),
    }));
  };

  const checkAutoScavenge = () => {
    if (waitingForScavengePopup) return;

    const now = Date.now();

    setLocations((prev) => {
      let hasActiveScavenge = false;
      let updated = false;

      const newLocations = prev.map((loc) => {
        if (loc.lastScavenged && loc.scavengeTime > 0) {
          if (now - loc.lastScavenged >= loc.scavengeTime && !updated) {
            const loot = {
              food: loc.resources.food || 0,
              water: loc.resources.water || 0,
              materials: loc.resources.materials || 0,
              survivors: loc.resources.survivors || 0,
            };

            finalizeScavenge(loc);
            updated = true;
            return loc;
          }

          hasActiveScavenge = true;
          return loc;
        }

        return loc;
      });

      if (!hasActiveScavenge && !updated) {
        // Start auto-scavenge on the first available location
        const targetIndex = newLocations.findIndex(
          (loc) =>
            loc.discovered &&
            !loc.lastScavenged &&
            loc.scavengeTime > 0 &&
            Object.values(loc.resources).some((v) => v && v > 0),
        );

        if (targetIndex !== -1) {
          newLocations[targetIndex] = {
            ...newLocations[targetIndex],
            lastScavenged: now,
          };
          updated = true;
        }
      }

      return newLocations;
    });
  };

  const finalizeScavenge = (location: MapLocation) => {
    const loot = {
      food: location.resources.food || 0,
      water: location.resources.water || 0,
      materials: location.resources.materials || 0,
      survivors: location.resources.survivors || 0,
    };

    setScavengeResult(loot);
    setResources((prevResources) => ({
      food: prevResources.food + loot.food,
      water: prevResources.water + loot.water,
      materials: prevResources.materials + loot.materials,
      survivors: prevResources.survivors + loot.survivors,
    }));
    setWaitingForScavengePopup(true);

    setLocations((prev) =>
      prev.map((loc) =>
        loc.id === location.id
          ? { ...loc, lastScavenged: null, resources: {}, wasScavenged: true }
          : loc,
      ),
    );
  };

  const handleMapClick = (
    e: React.MouseEvent<HTMLDivElement>,
    location: MapLocation,
  ) => {
    e.stopPropagation();
    if (!location.discovered && location.id !== "base") return;

    setSelectedLocation(location);
    setRadialMenuPosition({ x: e.clientX, y: e.clientY });
    setShowRadialMenu(true);
  };

  const scavengeLocation = (location: MapLocation) => {
    if (location.lastScavenged || location.scavengeTime === 0) return;

    setLocations((prev) =>
      prev.map((loc) =>
        loc.id === location.id ? { ...loc, lastScavenged: Date.now() } : loc,
      ),
    );
    setShowRadialMenu(false);
  };

  const exploreNearby = () => {
    if (!selectedLocation) return;

    const cost = EXPLORATION_COST;
    const canAfford = Object.entries(cost).every(
      ([resource, amount]) => resources[resource as keyof Resources] >= (amount || 0)
    );

    if (!canAfford) {
      // Show a message that you can't afford to explore
      setShowAttackResult({
        success: false,
        locationName: "Exploration",
        survivorsLost: 0,
      });
      setShowRadialMenu(false);
      return;
    }

    // Deduct cost
    setResources(prev => {
      const newResources = { ...prev };
      Object.entries(cost).forEach(([resource, amount]) => {
        newResources[resource as keyof Resources] -= amount || 0;
      });
      return newResources;
    });

    const getGridCoords = (id: string) => {
      const match = id.match(/cell-(\d+)-(\d+)/);
      if (!match) return null;
      return { x: parseInt(match[1]), y: parseInt(match[2]) };
    };

    const selectedCoords = getGridCoords(selectedLocation.id);
    if (!selectedCoords) return;

    setLocations((prev) =>
      prev.map((loc) => {
        const coords = getGridCoords(loc.id);
        if (!coords || loc.discovered) return loc;

        const dx = Math.abs(coords.x - selectedCoords.x);
        const dy = Math.abs(coords.y - selectedCoords.y);

        if (dx <= 1 && dy <= 1) {
          return { ...loc, discovered: true };
        }

        return loc;
      }),
    );

    setShowRadialMenu(false);
  };

  const purchaseUpgrade = (upgrade: BaseUpgrade) => {
    if (upgrade.level >= upgrade.maxLevel) return;

    const canAfford = Object.entries(upgrade.cost).every(
      ([resource, cost]) =>
        resources[resource as keyof Resources] >= (cost || 0),
    );

    if (canAfford) {
      setResources((prev) => {
        const newResources = { ...prev };
        Object.entries(upgrade.cost).forEach(([resource, cost]) => {
          newResources[resource as keyof Resources] -= cost || 0;
        });
        return newResources;
      });

      setUpgrades((prev) =>
        prev.map((u) =>
          u.id === upgrade.id
            ? {
                ...u,
                level: u.level + 1,
                cost: Object.fromEntries(
                  Object.entries(u.cost).map(([r, c]) => [
                    r,
                    Math.floor((c || 0) * 1.5),
                  ]),
                ),
              }
            : u,
        ),
      );
    }
  };

  const getScavengeProgress = (location: MapLocation) => {
    if (!location.lastScavenged || location.scavengeTime === 0) return 0;
    const elapsed = Date.now() - location.lastScavenged;
    return Math.min(100, (elapsed / location.scavengeTime) * 100);
  };

  const attackLocation = (location: MapLocation) => {
    const success = Math.random() < 0.7; // Increased to 70% chance of success

    if (success) {
      const materialsGained = 20 + Math.floor(Math.random() * 30);
      setResources((prev) => ({
        ...prev,
        materials: prev.materials + materialsGained,
      }));
      setLocations((prev) =>
        prev.map((loc) =>
          loc.id === location.id ? { ...loc, owner: "player" } : loc,
        ),
      );
      setShowAttackResult({
        success: true,
        locationName: location.name,
        resourcesGained: { materials: materialsGained },
      });
      setShowAttackedAnimation(location.id); // Trigger red square animation
    } else {
      const survivorsLost = 1; // Always lose 1 survivor on failure
      setResources((prev) => ({
        ...prev,
        survivors: Math.max(0, prev.survivors - survivorsLost),
      }));
      setShowAttackResult({
        success: false,
        locationName: location.name,
        survivorsLost: survivorsLost,
      });
    }
    setShowRadialMenu(false);
  };

  const buyBackLocation = (location: MapLocation) => {
    const cost = { materials: 50 }; // Example cost

    const canAfford = Object.entries(cost).every(
      ([resource, amount]) => resources[resource as keyof Resources] >= (amount || 0)
    );

    if (canAfford) {
      setResources(prev => {
        const newResources = { ...prev };
        Object.entries(cost).forEach(([resource, amount]) => {
          newResources[resource as keyof Resources] -= amount || 0;
        });
        return newResources;
      });

      setLocations(prev =>
        prev.map(loc =>
          loc.id === location.id ? { ...loc, owner: "player" } : loc
        )
      );
      setShowAttackedAnimation(location.id); // Trigger red square animation
      setShowRadialMenu(false);
    } else {
      // Optionally, add feedback for not being able to afford
      console.log("Cannot afford to buy back this location.");
    }
  };

  // Add new state for first time user
  const [isFirstTimeUser, setIsFirstTimeUser] = useState(() => {
    const savedGame = localStorage.getItem(SAVE_KEY);
    return !savedGame;
  });

  // Add new state for game over
  const [isGameOver, setIsGameOver] = useState(false);

  // Update startNewGame function
  const startNewGame = () => {
    setResources(initialResources);
    setUpgrades(upgrades.map(upgrade => ({ ...upgrade, level: 0 })));
    setLocations(generateInitialGrid());
    setBuildings([]);
    setIsGameOver(false);
    setIsFirstTimeUser(false);
    setShowDisaster(null);
    setShowAttackResult(null);
    setShowAIAttack(null);
    setScavengeResult(null);
    setWaitingForScavengePopup(false);
    setShowAttackedAnimation(null);
    setSelectedLocation(null);
    setShowRadialMenu(false);
    localStorage.removeItem(SAVE_KEY); // Clear saved game from localStorage
  };

  // Clear attacked animation after a duration
  useEffect(() => {
    if (showAttackedAnimation) {
      const timer = setTimeout(() => {
        setShowAttackedAnimation(null);
      }, 1500); // Animation duration
      return () => clearTimeout(timer);
    }
  }, [showAttackedAnimation]);

  // Update GameOver component
  const GameOver = () => {
    if (!isGameOver) return null;

    return (
      <div className="fixed inset-0 bg-black bg-opacity-90 flex items-center justify-center z-50 p-4 pointer-events-auto">
        <div className="relative bg-gray-900 border border-red-700 rounded-xl p-8 max-w-md shadow-2xl text-center animate-fade-in">
          <h3 className="text-4xl font-bold text-red-500 mb-6 drop-shadow-md">
            Game Over
          </h3>
          <p className="text-gray-200 text-lg mb-8">
            All your survivors have perished. The wasteland has claimed another settlement.
          </p>
          <div className="space-y-4">
            <button
              onClick={startNewGame}
              className="w-full px-6 py-3 rounded-lg text-lg font-bold uppercase tracking-wider transition-all duration-300 transform hover:scale-105 shadow-md bg-gradient-to-r from-green-600 to-green-800 hover:from-green-700 hover:to-green-900 text-white"
            >
              Start New Game
            </button>
          </div>
        </div>
      </div>
    );
  };

  // Update NewGame component
  const NewGame = () => {
    if (!isFirstTimeUser) return null;

    return (
      <div className="fixed inset-0 bg-black bg-opacity-90 flex items-center justify-center z-50 p-4 pointer-events-auto">
        <div className="relative bg-gray-900 border border-blue-700 rounded-xl p-8 max-w-md shadow-2xl text-center animate-fade-in">
          <h3 className="text-4xl font-bold text-blue-500 mb-6 drop-shadow-md">
            Welcome to Wasteland Survivor
          </h3>
          <p className="text-gray-200 text-lg mb-8">
            Lead your survivors through the post-apocalyptic wasteland. Gather resources, expand your territory, and survive against all odds.
          </p>
          <div className="space-y-4">
            <button
              onClick={startNewGame}
              className="w-full px-6 py-3 rounded-lg text-lg font-bold uppercase tracking-wider transition-all duration-300 transform hover:scale-105 shadow-md bg-gradient-to-r from-blue-600 to-blue-800 hover:from-blue-700 hover:to-blue-900 text-white"
            >
              Start New Game
            </button>
          </div>
        </div>
      </div>
    );
  };

  // Update popup components to remove pointer-events-none
  const ScavengePopup = () => {
    if (!scavengeResult) return null;

    return (
      <div className="fixed inset-0 bg-black bg-opacity-70 flex items-center justify-center z-50 p-4">
        <div className="relative bg-gray-900 border border-gray-700 rounded-xl p-8 max-w-sm shadow-2xl text-center animate-fade-in">
          <h3 className="text-3xl font-bold text-green-500 mb-4 drop-shadow-md">
            Resources Found!
          </h3>
          <ul className="text-gray-200 text-lg space-y-2 mb-6">
            {Object.entries(scavengeResult).map(([key, value]) =>
              value > 0 ? (
                <li key={key} className="flex items-center justify-center">
                  <span className="capitalize mr-2">{key}:</span>
                  <span className="font-semibold text-white text-2xl">{value}</span>
                </li>
              ) : null,
            )}
          </ul>
          <button
            onClick={() => {
              setScavengeResult(null);
              setWaitingForScavengePopup(false); // Enables next scavenge
            }}
            className="w-full px-6 py-3 rounded-lg text-lg font-bold uppercase tracking-wider transition-all duration-300 transform hover:scale-105 shadow-md bg-gradient-to-r from-green-600 to-green-800 hover:from-green-700 hover:to-green-900 text-white"
          >
            Continue
          </button>
        </div>
      </div>
    );
  };

  const DisasterPopup = () => {
    if (!showDisaster) return null;

    return (
      <div className="fixed inset-0 bg-black bg-opacity-70 flex items-center justify-center z-50 p-4">
        <div className="relative bg-gray-900 border border-red-700 rounded-xl p-8 max-w-sm shadow-2xl text-center animate-fade-in">
          <h3 className="text-3xl font-bold text-red-500 mb-4 drop-shadow-md">
            {showDisaster.name}
          </h3>
          <p className="text-gray-200 text-lg mb-6">{showDisaster.description}</p>
          <div className="text-lg text-gray-300 space-y-2 mb-6">
            {Object.entries(showDisaster.effect).map(([resource, amount]) => (
              <p key={resource} className="flex items-center justify-center">
                <span className="capitalize mr-2">{resource}:</span>
                <span className="font-semibold text-white text-2xl">{amount}</span>
              </p>
            ))}
          </div>
          <button
            onClick={() => setShowDisaster(null)}
            className="w-full px-6 py-3 rounded-lg text-lg font-bold uppercase tracking-wider transition-all duration-300 transform hover:scale-105 shadow-md bg-gradient-to-r from-red-600 to-red-800 hover:from-red-700 hover:to-red-900 text-white"
          >
            Continue
          </button>
        </div>
      </div>
    );
  };

  const AttackResultPopup = () => {
    if (!showAttackResult) return null;

    const isSuccess = showAttackResult.success;

    return (
      <div className="fixed inset-0 bg-black bg-opacity-70 flex items-center justify-center z-50 p-4">
        <div className={`relative bg-gray-900 border ${isSuccess ? "border-green-700" : "border-red-700"} rounded-xl p-8 max-w-sm shadow-2xl text-center animate-fade-in`}>
          <h3 className={`text-3xl font-bold ${isSuccess ? "text-green-500" : "text-red-500"} mb-4 drop-shadow-md`}>
            {isSuccess ? "Attack Successful!" : "Attack Failed!"}
          </h3>
          <p className="text-gray-200 text-lg mb-6">
            {isSuccess ? (
              <>
                You successfully took over the <span className="font-semibold">{showAttackResult.locationName}</span>! You gained:
              </>
            ) : (
              <>
                Your attack on <span className="font-semibold">{showAttackResult.locationName}</span> failed! You lost:
              </>
            )}
          </p>
          <ul className="text-gray-200 text-lg space-y-2 mb-6">
            {isSuccess && showAttackResult.resourcesGained ? (
              Object.entries(showAttackResult.resourcesGained).map(([key, value]) =>
                value && value > 0 ? (
                  <li key={key} className="flex items-center justify-center">
                    <span className="capitalize mr-2">{key}:</span>
                    <span className="font-semibold text-white text-2xl">{value}</span>
                  </li>
                ) : null,
              )
            ) : null}
            {!isSuccess && showAttackResult.survivorsLost ? (
              <li className="flex items-center justify-center">
                <span className="capitalize mr-2">Survivors:</span>
                <span className="font-semibold text-white text-2xl">{showAttackResult.survivorsLost}</span>
              </li>
            ) : null}
          </ul>
          <button
            onClick={() => setShowAttackResult(null)}
            className={`w-full px-6 py-3 rounded-lg text-lg font-bold uppercase tracking-wider transition-all duration-300 transform hover:scale-105 shadow-md ${isSuccess
              ? "bg-gradient-to-r from-green-600 to-green-800 hover:from-green-700 hover:to-green-900 text-white"
              : "bg-gradient-to-r from-red-600 to-red-800 hover:from-red-700 hover:to-red-900 text-white"}
            `}
          >
            Continue
          </button>
        </div>
      </div>
    );
  };

  const AIAttackPopup = () => {
    if (!showAIAttack) return null;

    const isSuccess = showAIAttack.success;

    return (
      <div className="fixed inset-0 bg-black bg-opacity-70 flex items-center justify-center z-50 p-4">
        <div className={`relative bg-gray-900 border ${isSuccess ? "border-green-700" : "border-red-700"} rounded-xl p-8 max-w-sm shadow-2xl text-center animate-fade-in`}>
          <h3 className={`text-3xl font-bold ${isSuccess ? "text-green-500" : "text-red-500"} mb-4 drop-shadow-md`}>
            {isSuccess ? "Rival Clan Attack Successful!" : "Rival Clan Attack Failed!"}
          </h3>
          <p className="text-gray-200 text-lg mb-6">
            {isSuccess ? (
              <>
                AI successfully took over the <span className="font-semibold">{showAIAttack.locationName}</span>! You lost:
              </>
            ) : (
              <>
                AI attack on <span className="font-semibold">{showAIAttack.locationName}</span> failed! You lost:
              </>
            )}
          </p>
          <ul className="text-gray-200 text-lg space-y-2 mb-6">
            {isSuccess && showAIAttack.survivorsLost ? (
              <li className="flex items-center justify-center">
                <span className="capitalize mr-2">Survivors:</span>
                <span className="font-semibold text-white text-2xl">{showAIAttack.survivorsLost}</span>
              </li>
            ) : null}
          </ul>
          <button
            onClick={() => setShowAIAttack(null)}
            className={`w-full px-6 py-3 rounded-lg text-lg font-bold uppercase tracking-wider transition-all duration-300 transform hover:scale-105 shadow-md ${isSuccess
              ? "bg-gradient-to-r from-green-600 to-green-800 hover:from-green-700 hover:to-green-900 text-white"
              : "bg-gradient-to-r from-red-600 to-red-800 hover:from-red-700 hover:to-red-900 text-white"}
            `}
          >
            Continue
          </button>
        </div>
      </div>
    );
  };

  // Update MapView to use built-in animation
  const MapView = ({
    buildings,
    availableBuildings,
    setBuildings,
    setResources,
    locations,
    showAttackedAnimation,
  }: {
    buildings: Building[];
    availableBuildings: Building[];
    setBuildings: React.Dispatch<React.SetStateAction<Building[]>>;
    setResources: React.Dispatch<React.SetStateAction<Resources>>;
    locations: MapLocation[];
    showAttackedAnimation: string | null;
  }) => {
    const handleDragOver = (e: React.DragEvent) => {
      e.preventDefault(); // Allow dropping
    };

    const handleDrop = (e: React.DragEvent, locationId: string) => {
      e.preventDefault();
      const buildingId = e.dataTransfer.getData("buildingId");
      const buildingBlueprint = availableBuildings.find((b) => b.id === buildingId);
      const targetLocation = locations.find((loc) => loc.id === locationId);

      if (!buildingBlueprint || !targetLocation) return;

      // Check if location is valid for building (player-owned, not base, no existing building)
      const isLocationOccupied = buildings.some(b => b.locationId === locationId);
      if (!targetLocation.discovered || targetLocation.owner !== "player" || targetLocation.isBase || isLocationOccupied) {
        console.log("Cannot place building here.");
        return;
      }

      // Check if player can afford the building
      const canAfford = Object.entries(buildingBlueprint.cost).every(
        ([resource, cost]) => resources[resource as keyof Resources] >= (cost || 0)
      );

      if (!canAfford) {
        console.log("Cannot afford this building.");
        return;
      }

      // Deduct resources
      setResources(prev => {
        const newResources = { ...prev };
        Object.entries(buildingBlueprint.cost).forEach(([resource, cost]) => {
          newResources[resource as keyof Resources] -= cost || 0;
        });
        return newResources;
      });

      // Add building to state
      const newBuilding: Building = {
        ...buildingBlueprint,
        id: `${buildingBlueprint.type}-${Date.now()}`, // Unique ID for placed building
        locationId: locationId,
      };
      setBuildings(prev => [...prev, newBuilding]);
    };

    return (
      <div className="relative bg-gray-900 border border-gray-700 rounded-xl p-8 shadow-2xl h-full flex flex-col overflow-hidden before:absolute before:inset-0 before:bg-gradient-to-br before:from-gray-800 before:via-gray-900 before:to-black before:opacity-50 before:rounded-xl">
        <h2 className="relative z-10 text-4xl font-extrabold text-white mb-8 text-center drop-shadow-md font-display">
          Wasteland Map
        </h2>
        <div
          className="relative z-10 grid bg-gray-950 border border-gray-700 rounded-lg aspect-square shadow-inner overflow-hidden"
          style={{
            gridTemplateColumns: `repeat(${GRID_SIZE}, 1fr)`,
            gridTemplateRows: `repeat(${GRID_SIZE}, 1fr)`,
          }}
          onClick={() => setShowRadialMenu(false)}
        >
          {locations.map((location) => {
            const isAnimatingTakeover = showAttackedAnimation === location.id;
            const placedBuilding = buildings.find(b => b.locationId === location.id);

            return (
              <div
                key={location.id}
                className={`relative flex items-center justify-center p-1 transition-all duration-300 group ${location.discovered ? "cursor-pointer" : "opacity-30"} ${isAnimatingTakeover ? 'animate-pulse' : ''} ${location.id === showAttackedAnimation ? 'border-4 border-red-500 animate-pulse-fast' : ''}`}
                onClick={(e) => handleMapClick(e, location)}
                onDragOver={handleDragOver} // Enable drag over
                onDrop={(e) => handleDrop(e, location.id)} // Handle drop
              >
                {location.discovered ? (
                  <div className="relative w-full h-full flex flex-col items-center justify-center text-center rounded-md overflow-hidden shadow-md group-hover:shadow-lg transition-shadow duration-300 border-2 border-transparent group-hover:border-blue-500">
                    <div
                      className={`w-full h-full flex items-center justify-center text-4xl transform transition-all duration-300 group-hover:scale-105 ${location.isBase || location.owner === "player" ? "bg-green-700" : "bg-blue-700"}`}
                    >
                      {placedBuilding ? (
                        placedBuilding.imageUrl ? (
                          <img src={placedBuilding.imageUrl} alt={placedBuilding.name} className="w-10 h-10 object-contain mx-auto" />
                        ) : (
                          <BaseIcon />
                        )
                      ) : (
                        location.isBase ? (
                          <BaseIcon />
                        ) : location.owner === "player" ? (
                          <ResourcesIcon />
                        ) : location.resources && Object.values(location.resources).some(val => val && val > 0) ? (
                          <ResourcesIcon />
                        ) : null
                      )}
                    </div>
                    <div className="absolute bottom-0 left-0 right-0 bg-gray-800 bg-opacity-80 p-1 text-center backdrop-blur-sm group-hover:bg-opacity-90 transition-opacity">
                      <p className="text-xs font-semibold text-gray-200 truncate font-display">
                        {location.name}
                      </p>
                    </div>
                    {location.lastScavenged && location.scavengeTime > 0 && (
                      <div className="absolute top-0 left-0 right-0 h-1 bg-gray-700 rounded-t">
                        <div
                          className="h-full bg-yellow-500 rounded-t transition-all duration-1000"
                          style={{ width: `${getScavengeProgress(location)}%` }}
                        />
                      </div>
                    )}
                  </div>
                ) : (
                  <div className="w-full h-full rounded-md bg-gray-800 flex items-center justify-center text-4xl opacity-50 animate-pulse border border-gray-700"></div>
                )}
              </div>
            );
          })}
        </div>
      </div>
    );
  };

  // Components
  const ResourcePanel = React.memo(
    ({
      resources,
      upgrades,
      autoScavengeEnabled,
      purchaseUpgrade,
      setAutoScavengeEnabled,
      availableBuildings,
    }: {
      resources: Resources;
      upgrades: BaseUpgrade[];
      autoScavengeEnabled: boolean;
      purchaseUpgrade: (upgrade: BaseUpgrade) => void;
      setAutoScavengeEnabled: (enabled: boolean) => void;
      availableBuildings: Building[];
    }) => {
      const onDragStart = (e: React.DragEvent, buildingId: string) => {
        e.dataTransfer.setData("buildingId", buildingId);
      };

      return (
        <div className="relative bg-gray-900 border border-gray-700 rounded-xl p-8 shadow-2xl h-full flex flex-col overflow-hidden before:absolute before:inset-0 before:bg-gradient-to-br before:from-gray-800 before:via-gray-900 before:to-black before:opacity-50 before:rounded-xl">
          <div className="relative z-10 flex-none">
            <h2 className="text-4xl font-extrabold text-white mb-8 text-center drop-shadow-md font-display">
              Resources
            </h2>
            <div className="grid grid-cols-2 lg:grid-cols-4 gap-6 mb-10">
              <div className="relative bg-gray-800 bg-opacity-70 backdrop-filter backdrop-blur-sm rounded-lg p-5 shadow-lg hover:shadow-xl transition-all duration-300 border border-gray-700 flex flex-col items-center justify-center space-y-2">
                <FoodIcon />
                <span className="text-lg font-bold text-gray-100 font-display">Food</span>
                <span className="text-3xl font-semibold text-green-400 font-mono">
                  {Math.floor(resources.food)}
                </span>
              </div>
              <div className="relative bg-gray-800 bg-opacity-70 backdrop-filter backdrop-blur-sm rounded-lg p-5 shadow-lg hover:shadow-xl transition-all duration-300 border border-gray-700 flex flex-col items-center justify-center space-y-2">
                <WaterIcon />
                <span className="text-lg font-bold text-gray-100 font-display">Water</span>
                <span className="text-3xl font-semibold text-blue-400 font-mono">
                  {Math.floor(resources.water)}
                </span>
              </div>
              <div className="relative bg-gray-800 bg-opacity-70 backdrop-filter backdrop-blur-sm rounded-lg p-5 shadow-lg hover:shadow-xl transition-all duration-300 border border-gray-700 flex flex-col items-center justify-center space-y-2">
                <MaterialsIcon />
                <span className="text-lg font-bold text-gray-100 font-display">Materials</span>
                <span className="text-3xl font-semibold text-orange-400 font-mono">
                  {Math.floor(resources.materials)}
                </span>
              </div>
              <div className="relative bg-gray-800 bg-opacity-70 backdrop-filter backdrop-blur-sm rounded-lg p-5 shadow-lg hover:shadow-xl transition-all duration-300 border border-gray-700 flex flex-col items-center justify-center space-y-2">
                <SurvivorsIcon />
                <span className="text-lg font-bold text-gray-100 font-display">Survivors</span>
                <span className="text-3xl font-semibold text-purple-400 font-mono">
                  {resources.survivors}
                </span>
              </div>
            </div>
            <h3 className="text-3xl font-bold text-white mb-6 text-center drop-shadow-md">
              Base Upgrades
            </h3>
          </div>

          <div className="relative z-10 flex-grow overflow-y-auto pr-4 -mr-4"> {/* Added pr-4 -mr-4 for scrollbar */}
            <div className="space-y-4">
              {upgrades.map((upgrade) => {
                const canAfford = Object.entries(upgrade.cost).every(
                  ([resource, cost]) =>
                    resources[resource as keyof Resources] >= (cost || 0),
                );

                return (
                  <div
                    key={upgrade.id}
                    className="bg-gray-800 bg-opacity-60 backdrop-filter backdrop-blur-sm rounded-xl p-5 shadow-lg flex flex-col md:flex-row items-center gap-4 border border-gray-700 hover:shadow-xl transition-shadow"
                  >
                    <div className="flex-grow text-center md:text-left">
                      <div className="flex flex-col md:flex-row items-center gap-2 mb-1">
                        <h4 className="text-xl font-semibold text-white">
                          {upgrade.name}
                        </h4>
                        <p className="text-sm text-green-300">
                          {upgrade.effect}
                        </p>
                      </div>
                      <p className="text-sm text-gray-300 mb-2">
                        {upgrade.description}
                      </p>
                      <p className="text-md font-medium text-gray-200">
                        Level {upgrade.level}/{upgrade.maxLevel}
                      </p>
                    </div>
                    {upgrade.level < upgrade.maxLevel && (
                      <button
                        onClick={() => purchaseUpgrade(upgrade)}
                        disabled={!canAfford}
                        className={`w-full md:w-auto px-6 py-3 rounded-lg text-lg font-bold uppercase tracking-wider transition-all duration-300 transform hover:scale-105 shadow-md ${
                          canAfford
                            ? "bg-gradient-to-r from-green-600 to-green-800 hover:from-green-700 hover:to-green-900 text-white"
                            : "bg-gray-600 text-gray-400 cursor-not-allowed opacity-70"
                        }`}
                      >
                        <p>Upgrade</p>
                        <p className="text-xs mt-1 opacity-80">
                          (
                          {Object.entries(upgrade.cost)
                            .map(([r, c]) => `${c} ${r}`)
                            .join(", ")}
                          )
                        </p>
                      </button>
                    )}
                  </div>
                );
              })}
            </div>

            <h3 className="text-3xl font-bold text-white mt-10 mb-6 text-center drop-shadow-md">
              Available Buildings
            </h3>
            <div className="grid grid-cols-2 gap-4">
              {availableBuildings.map((building) => {
                const canAffordBuilding = Object.entries(building.cost).every(
                  ([resource, cost]) => resources[resource as keyof Resources] >= (cost || 0)
                );
                return (
                  <div
                    key={building.id}
                    draggable
                    onDragStart={(e) => onDragStart(e, building.id)}
                    className={`bg-gray-800 bg-opacity-60 backdrop-filter backdrop-blur-sm rounded-xl p-4 shadow-lg flex flex-col items-center text-center border border-gray-700 transition-all duration-300
                      ${canAffordBuilding ? "cursor-grab active:cursor-grabbing hover:shadow-xl" : "opacity-50 cursor-not-allowed"}
                    `}
                  >
                    {building.imageUrl ? (
                      <img src={building.imageUrl} alt={building.name} className="w-16 h-16 object-cover mb-2" />
                    ) : (
                      <span className="text-4xl mb-2">🏗️</span>
                    )}
                    <h4 className="text-xl font-semibold text-white mb-1">{building.name}</h4>
                    <p className="text-sm text-gray-300 mb-2">
                      Cost: {Object.entries(building.cost).map(([r, c]) => `${c} ${r}`).join(", ")}
                    </p>
                    {!canAffordBuilding && (
                      <p className="text-red-400 text-xs">Cannot afford</p>
                    )}
                  </div>
                );
              })}
            </div>
          </div>

          <div className="relative z-10 flex-none mt-8 pt-6 border-t border-gray-700">
            <label className="flex items-center justify-center space-x-3 text-gray-300 cursor-pointer">
              <input
                type="checkbox"
                checked={autoScavengeEnabled}
                onChange={(e) => setAutoScavengeEnabled(e.target.checked)}
                className="form-checkbox h-5 w-5 text-green-500 bg-gray-700 border-gray-600 rounded focus:ring-green-500"
              />
              <span className="text-lg font-medium text-white">Auto-scavenge locations</span>
            </label>
          </div>
          <div className="relative z-10 flex-none mt-8 pt-6 border-t border-gray-700">
            <button
              onClick={startNewGame}
              className="w-full px-6 py-3 rounded-lg text-lg font-bold uppercase tracking-wider transition-all duration-300 transform hover:scale-105 shadow-md bg-gradient-to-r from-red-600 to-red-800 hover:from-red-700 hover:to-red-900 text-white"
            >
              Reset Game
            </button>
          </div>
        </div>
      );
    },
  );

  // Components
  const RadialMenu = () => {
    if (!showRadialMenu || !selectedLocation) return null;

    const actions: RadialMenuAction[] = [
      {
        label: "Scavenge",
        icon: <ResourcesIcon />,
        enabled:
          !selectedLocation.lastScavenged &&
          selectedLocation.scavengeTime > 0 &&
          Object.values(selectedLocation.resources).some(
            (val) => val && val > 0,
          ),
        action: () => scavengeLocation(selectedLocation),
      },
      {
        label: "Attack",
        icon: <AttackIcon />,
        enabled: 
          selectedLocation.discovered && 
          !selectedLocation.isBase &&
          selectedLocation.owner !== "player",
        action: () => attackLocation(selectedLocation),
      },
      {
        label: "Buy Back",
        icon: "💸",
        enabled:
          selectedLocation.discovered &&
          !selectedLocation.isBase &&
          selectedLocation.owner !== "player" &&
          resources.materials >= 50,
        action: () => buyBackLocation(selectedLocation),
        cost: { materials: 50 },
      },
      {
        label: "Explore",
        icon: <ExploreIcon />,
        enabled: resources.materials >= EXPLORATION_COST.materials && resources.survivors >= EXPLORATION_COST.survivors,
        action: exploreNearby,
        cost: EXPLORATION_COST,
      },
      {
        label: "Cancel",
        icon: <CancelIcon />,
        enabled: true,
        action: () => setShowRadialMenu(false),
      },
    ];

    return (
      <div
        className="absolute"
        style={{ left: radialMenuPosition.x, top: radialMenuPosition.y }}
      >
        {actions.map((action, index) => {
          const angle = (index * 120 - 60) * (Math.PI / 180);
          const x = Math.cos(angle) * 100;
          const y = Math.sin(angle) * 100;

          return (
            <button
              key={action.label}
              disabled={!action.enabled}
              onClick={(e) => {
                e.stopPropagation();
                action.action();
              }}
              className={`absolute transform -translate-x-1/2 -translate-y-1/2 w-24 h-24 rounded-full flex flex-col items-center justify-center text-white text-sm font-bold uppercase tracking-wider transition-all duration-300 shadow-xl border-2 ${
                action.enabled
                  ? "bg-gradient-to-br from-blue-600 to-blue-800 hover:from-blue-700 hover:to-blue-900 border-blue-500 hover:border-blue-300 transform hover:scale-110"
                  : "bg-gray-700 border-gray-600 cursor-not-allowed opacity-60"
              }`}
              style={{ left: x, top: y }}
            >
              <div className="text-3xl mb-1">{action.icon}</div>
              <span className="font-display">{action.label}</span>
              {action.cost && (
                <div className="text-xs mt-1 text-gray-300">
                  {action.cost.materials !== undefined && (
                    <div className="flex items-center justify-center">
                      <span className="mr-1">
                        <MaterialsIcon className="h-4 w-4" />
                      </span>
                      <span className="font-mono">{action.cost.materials}</span>
                    </div>
                  )}
                  {action.cost.survivors !== undefined && (
                    <div className="flex items-center justify-center">
                      <span className="mr-1">
                        <SurvivorsIcon className="h-4 w-4" />
                      </span>
                      <span className="font-mono">{action.cost.survivors}</span>
                    </div>
                  )}
                </div>
              )}
            </button>
          );
        })}
      </div>
    );
  };

  // Add game over check to the game loop
  useEffect(() => {
    if (resources.survivors <= 0 && !isGameOver) {
      setIsGameOver(true);
    }
  }, [resources.survivors, isGameOver]);

  // Add back the triggerAIAttack function
  const triggerAIAttack = () => {
    const playerOwnedLocations = locations.filter(
      (loc) => loc.owner === "player" && !loc.isBase
    );

    if (playerOwnedLocations.length > 0) {
      const targetLocation =
        playerOwnedLocations[Math.floor(Math.random() * playerOwnedLocations.length)];
      const success = Math.random() < 0.75; // Increased AI attack success chance

      setShowAttackedAnimation(targetLocation.id); // Trigger red square animation

      if (success) {
        // AI takes over the location
        setLocations((prev) =>
          prev.map((loc) =>
            loc.id === targetLocation.id ? { ...loc, owner: null } : loc
          )
        );
        setResources((prev) => ({
          ...prev,
          survivors: Math.max(0, prev.survivors - 1),
        }));
        setShowAIAttack({
          locationId: targetLocation.id,
          locationName: targetLocation.name,
          success: true,
          survivorsLost: 1,
        });
      } else {
        // Player successfully defended
        setShowAIAttack({
          locationId: targetLocation.id,
          locationName: targetLocation.name,
          success: false,
        });
      }
    }
  };

  // HowToPlay component
  const HowToPlay = () => {
    if (!showHowToPlay) return null;

    return (
      <div className="fixed inset-0 bg-black bg-opacity-90 flex items-center justify-center z-50 p-4 pointer-events-auto">
        <div className="relative bg-gray-900 border border-blue-700 rounded-xl p-8 max-w-2xl shadow-2xl text-center animate-fade-in">
          <h3 className="text-4xl font-bold text-blue-500 mb-6 drop-shadow-md font-display">
            How to Play
          </h3>
          <div className="text-gray-200 text-lg space-y-4 text-left">
            <p>
              Welcome to Wasteland Survivor! Your goal is to lead your group of survivors through a post-apocalyptic world. Here's how to play:
            </p>
            <h4 className="text-xl font-semibold text-blue-300 mt-4">Resources:</h4>
            <p>Gather Food 🍖, Water 💧, Materials 🔧, and manage your Survivors 👥. These are essential for survival and progress.</p>

            <h4 className="text-xl font-semibold text-blue-300 mt-4">Map & Exploration:</h4>
            <p>Click on map locations to open a radial menu. You can Scavenge locations for resources, Attack rival locations to take them over, or Explore nearby to discover new areas. Exploring costs materials and survivors.</p>

            <h4 className="text-xl font-semibold text-blue-300 mt-4">Buildings:</h4>
            <p>From the Resources panel, you can drag and drop buildings like Houses onto available map squares. Houses provide shelter for your survivors; without enough housing, survivors will freeze and die!</p>

            <h4 className="text-xl font-semibold text-blue-300 mt-4">Upgrades:</h4>
            <p>Invest in base upgrades to increase your resource production and survivor attraction.</p>

            <h4 className="text-xl font-semibold text-blue-300 mt-4">Threats:</h4>
            <p>Beware of random Disasters and Rival Clan attacks. These events can deplete your resources and survivors, so be prepared!</p>

            <h4 className="text-xl font-semibold text-blue-300 mt-4">Game Over:</h4>
            <p>The game ends if all your survivors are lost. You can always start a new game to try again!</p>
          </div>
          <button
            onClick={() => setShowHowToPlay(false)}
            className="w-full mt-8 px-6 py-3 rounded-lg text-lg font-bold uppercase tracking-wider transition-all duration-300 transform hover:scale-105 shadow-md bg-gradient-to-r from-blue-600 to-blue-800 hover:from-blue-700 hover:to-blue-900 text-white"
          >
            Got It!
          </button>
        </div>
      </div>
    );
  };

  return (
    <div className="min-h-screen bg-gray-950 text-white font-sans antialiased relative overflow-hidden">
      {/* Background overlay */}
      <div 
        className="fixed inset-0 z-0 opacity-10 pointer-events-none" 
        style={{ 
          backgroundImage: `url('https://www.transparenttextures.com/patterns/dust-and-stones.png')`, 
          backgroundSize: 'cover',
          backgroundAttachment: 'fixed'
        }}
      ></div>

      {!isGameOver && (
        <>
          {isMobile ? (
            // Mobile layout with tabs
            <div className="relative z-10 px-4 py-6 h-screen flex flex-col bg-gray-950 bg-opacity-95 pointer-events-auto">
              <h1 className="text-5xl font-extrabold text-center mb-8 text-white drop-shadow-lg leading-tight font-display">
                Wasteland
                <br />
                Survivor
              </h1>
              <div className="flex mb-6 bg-gray-800 rounded-xl p-1 shadow-inner border border-gray-700">
                <button
                  onClick={() => setActiveTab("resources")}
                  className={`flex-1 py-3 px-4 rounded-lg text-lg font-bold transition-all duration-300 transform hover:scale-105 shadow-md ${
                    activeTab === "resources"
                      ? "bg-gradient-to-r from-blue-600 to-blue-800 text-white" : "text-gray-400 hover:text-white hover:bg-gray-700"
                  }`}
                >
                  Resources
                </button>
                <button
                  onClick={() => setActiveTab("map")}
                  className={`flex-1 py-3 px-4 rounded-lg text-lg font-bold transition-all duration-300 transform hover:scale-105 shadow-md ${
                    activeTab === "map"
                      ? "bg-gradient-to-r from-blue-600 to-blue-800 text-white" : "text-gray-400 hover:text-white hover:bg-gray-700"
                  }`}
                >
                  Map
                </button>
              </div>
              {activeTab === "resources" ? (
                <ResourcePanel
                  resources={resources}
                  upgrades={upgrades}
                  autoScavengeEnabled={autoScavengeEnabled}
                  purchaseUpgrade={purchaseUpgrade}
                  setAutoScavengeEnabled={setAutoScavengeEnabled}
                  availableBuildings={availableBuildings}
                />
              ) : (
                <MapView
                  buildings={buildings}
                  availableBuildings={availableBuildings}
                  setBuildings={setBuildings}
                  setResources={setResources}
                  locations={locations}
                  showAttackedAnimation={showAttackedAnimation}
                />
              )}
            </div>
          ) : (
            // Desktop split-screen layout
            <div className="relative z-10 flex h-screen p-8 gap-8 bg-gray-950 bg-opacity-95">
              <div className="w-1/2 flex flex-col">
                <h1 className="text-5xl font-extrabold mb-8 text-white drop-shadow-lg leading-tight text-center font-display">
                  Wasteland
                  <br />
                  Survivor
                </h1>
                <ResourcePanel
                  resources={resources}
                  upgrades={upgrades}
                  autoScavengeEnabled={autoScavengeEnabled}
                  purchaseUpgrade={purchaseUpgrade}
                  setAutoScavengeEnabled={setAutoScavengeEnabled}
                  availableBuildings={availableBuildings}
                />
              </div>
              <div className="w-1/2 flex flex-col h-full">
                <MapView
                  buildings={buildings}
                  availableBuildings={availableBuildings}
                  setBuildings={setBuildings}
                  setResources={setResources}
                  locations={locations}
                  showAttackedAnimation={showAttackedAnimation}
                />
              </div>
            </div>
          )}
        </>
      )}

      {/* Global How to Play button */}
      {!isGameOver && !isFirstTimeUser && (
        <button
          onClick={() => setShowHowToPlay(true)}
          className="fixed bottom-4 right-4 px-6 py-3 rounded-lg text-lg font-bold uppercase tracking-wider transition-all duration-300 transform hover:scale-105 shadow-md bg-gradient-to-r from-purple-600 to-purple-800 hover:from-purple-700 hover:to-purple-900 text-white z-40"
        >
          How to Play
        </button>
      )}

      {/* Conditionally render the popup container */}
      {(showRadialMenu || showDisaster || scavengeResult || showAttackResult || showAIAttack || isGameOver || isFirstTimeUser || showHowToPlay) && (
        <div 
          className="fixed inset-0 z-50 bg-black bg-opacity-70 flex items-center justify-center pointer-events-auto" 
          onClick={(e) => { 
            if (e.target === e.currentTarget) { // Only close if clicking the backdrop, not content
              setShowRadialMenu(false);
              setShowDisaster(null);
              setScavengeResult(null);
              setShowAttackResult(null);
              setShowAIAttack(null);
              setShowHowToPlay(false);
            }
          }}
        >
          <RadialMenu />
          <DisasterPopup />
          <ScavengePopup />
          <AttackResultPopup />
          <AIAttackPopup />
          <GameOver />
          <NewGame />
          <HowToPlay />
        </div>
      )}
    </div>
  );
};

export default ApocalypticGame;
