import React, { useCallback, useEffect, useRef, useState } from "react";

// Types
interface Resources {
  food: number;
  water: number;
  materials: number;
  survivors: number;
}

interface BaseUpgrade {
  id: string;
  name: string;
  description: string;
  cost: Partial<Resources>;
  effect: string;
  level: number;
  maxLevel: number;
}

interface MapLocation {
  id: string;
  name: string;
  x: number;
  y: number;
  discovered: boolean;
  scavengeTime: number;
  lastScavenged: number | null;
  resources: Partial<Resources>;
  isBase?: boolean; // Optional property to mark the base location
  wasScavenged?: boolean; // Optional property to track if the location was already scavenged
}

interface Disaster {
  id: string;
  name: string;
  description: string;
  effect: Partial<Resources>;
}

interface GameState {
  resources: Resources;
  upgrades: BaseUpgrade[];
  locations: MapLocation[];
  lastSave: number;
  autoScavengeEnabled: boolean;
}

// Constants
const SAVE_KEY = "apocalypse-idle-save";
const AUTO_SAVE_INTERVAL = 10000; // 10 seconds
const DISASTER_CHANCE = 0.002; // 0.2% chance per second

const initialResources: Resources = {
  food: 100,
  water: 100,
  materials: 50,
  survivors: 5,
};

const disasters: Disaster[] = [
  {
    id: "raiders",
    name: "Raider Attack!",
    description: "Raiders have attacked your base!",
    effect: { food: -20, water: -15, materials: -10 },
  },
  {
    id: "drought",
    name: "Drought",
    description: "Water supplies are running low!",
    effect: { water: -30 },
  },
  {
    id: "sickness",
    name: "Disease Outbreak",
    description: "A disease has spread through your camp!",
    effect: { survivors: -1, food: -10 },
  },
];

const ApocalypticGame: React.FC = () => {
  // State
  const [resources, setResources] = useState<Resources>(initialResources);
  const [activeTab, setActiveTab] = useState<"resources" | "map">("resources");
  const [isMobile, setIsMobile] = useState(false);
  const [showRadialMenu, setShowRadialMenu] = useState(false);
  const [radialMenuPosition, setRadialMenuPosition] = useState({ x: 0, y: 0 });
  const [selectedLocation, setSelectedLocation] = useState<MapLocation | null>(
    null,
  );
  const [showDisaster, setShowDisaster] = useState<Disaster | null>(null);
  const [autoScavengeEnabled, setAutoScavengeEnabled] = useState(true);

  const [scavengeResult, setScavengeResult] =
    useState<Partial<Resources> | null>(null);

  const [waitingForScavengePopup, setWaitingForScavengePopup] = useState(false);

  const gameLoopRef = useRef<ReturnType<typeof setInterval> | null>(null);
  const autoSaveRef = useRef<ReturnType<typeof setInterval> | null>(null);

  const [upgrades, setUpgrades] = useState<BaseUpgrade[]>([
    {
      id: "farm",
      name: "Makeshift Farm",
      description: "Produces food over time",
      cost: { materials: 50 },
      effect: "+2 food/minute",
      level: 0,
      maxLevel: 5,
    },
    {
      id: "well",
      name: "Water Collector",
      description: "Collects water from the atmosphere",
      cost: { materials: 40 },
      effect: "+1 water/minute",
      level: 0,
      maxLevel: 5,
    },
    {
      id: "shelter",
      name: "Survivor Shelter",
      description: "Attracts new survivors",
      cost: { materials: 100, food: 50 },
      effect: "+1 survivor chance/hour",
      level: 0,
      maxLevel: 3,
    },
  ]);

  const GRID_SIZE = 5;

  const locationNames = [
    "Dustfield",
    "Ashrock",
    "Ironpit",
    "Deadwell",
    "Cragpoint",
    "Burnpass",
    "Mudspire",
    "Wretchend",
    "Redhollow",
    "Saltmoor",
    "Scraptown",
    "Charcliff",
    "Dryreach",
    "Ragbank",
    "Blightden",
    "Boneplain",
    "Rustbarrow",
    "Smokefall",
    "Shivglen",
    "Flintgorge",
    "Gravefen",
    "Scorchbay",
    "Fogscar",
    "Ruinstep",
    "Crimsonlot",
  ];

  const generateInitialGrid = (): MapLocation[] => {
    const locations: MapLocation[] = [];

    for (let y = 0; y < GRID_SIZE; y++) {
      for (let x = 0; x < GRID_SIZE; x++) {
        const isBase =
          x === Math.floor(GRID_SIZE / 2) && y === Math.floor(GRID_SIZE / 2);

        locations.push({
          id: `cell-${x}-${y}`,
          name: isBase
            ? "Your Base"
            : locationNames[y * GRID_SIZE + x] || `Sector-${x}-${y}`,
          isBase: isBase,
          x,
          y,
          discovered: isBase,
          scavengeTime: isBase ? 0 : 5000 + Math.floor(Math.random() * 5000),
          lastScavenged: null,
          resources: isBase
            ? {}
            : Math.random() < 0.6 // change of having resoureces on newly discovered locations
            ? {
                food: Math.floor(Math.random() * 30),
                water: Math.floor(Math.random() * 20),
                materials:
                  Math.random() < 0.5 ? Math.floor(Math.random() * 40) : 0,
                survivors: Math.random() < 0.2 ? 1 : 0,
              }
            : {},
        });
      }
    }

    return locations;
  };

  const [locations, setLocations] = useState<MapLocation[]>(
    generateInitialGrid(),
  );

  const resourcesRef = useRef(resources);
  const upgradesRef = useRef(upgrades);

  // Update refs whenever state changes
  useEffect(() => {
    resourcesRef.current = resources;
  }, [resources]);
  useEffect(() => {
    upgradesRef.current = upgrades;
  }, [upgrades]);

  // Responsive design
  useEffect(() => {
    const checkMobile = () => {
      setIsMobile(window.innerWidth < 768);
    };
    checkMobile();
    window.addEventListener("resize", checkMobile);
    return () => window.removeEventListener("resize", checkMobile);
  }, []);

  // Load game
  useEffect(() => {
    const savedGame = localStorage.getItem(SAVE_KEY);
    if (savedGame) {
      try {
        const gameState: GameState = JSON.parse(savedGame);
        const timePassed = Date.now() - gameState.lastSave;

        // Calculate offline progression
        const minutesPassed = timePassed / 60000;
        const offlineResources = { ...gameState.resources };

        gameState.upgrades.forEach((upgrade) => {
          if (upgrade.id === "farm" && upgrade.level > 0) {
            offlineResources.food += Math.floor(
              minutesPassed * 2 * upgrade.level * 0.5,
            ); // 50% efficiency offline
          }
          if (upgrade.id === "well" && upgrade.level > 0) {
            offlineResources.water += Math.floor(
              minutesPassed * 1 * upgrade.level * 0.5,
            );
          }
          if (upgrade.id === "shelter" && upgrade.level > 0) {
            offlineResources.survivors += Math.floor(
              minutesPassed * (1 / 60) * upgrade.level * 0.5,
            );
          }
        });

        setResources(offlineResources);
        setUpgrades(gameState.upgrades);
        setLocations(gameState.locations);
        setAutoScavengeEnabled(gameState.autoScavengeEnabled);
      } catch (e) {
        console.error("Failed to load save:", e);
      }
    }
  }, []);

  // Save game
  const saveGame = useCallback(() => {
    const gameState: GameState = {
      resources,
      upgrades,
      locations,
      lastSave: Date.now(),
      autoScavengeEnabled,
    };
    localStorage.setItem(SAVE_KEY, JSON.stringify(gameState));
  }, [resources, upgrades, locations, autoScavengeEnabled]);

  // Auto save
  useEffect(() => {
    autoSaveRef.current = setInterval(saveGame, AUTO_SAVE_INTERVAL);
    return () => {
      if (autoSaveRef.current) clearInterval(autoSaveRef.current);
    };
  }, [saveGame]);

  function floorResources(res: Resources): Resources {
    return {
      food: Math.floor(res.food),
      water: Math.floor(res.water),
      materials: Math.floor(res.materials),
      survivors: Math.floor(res.survivors),
    };
  }

  function isEqualResources(a: Resources, b: Resources): boolean {
    return (
      a.food === b.food &&
      a.water === b.water &&
      a.materials === b.materials &&
      a.survivors === b.survivors
    );
  }

  // Game loop
  useEffect(() => {
    gameLoopRef.current = setInterval(() => {
      setResources((prev) => {
        const newResources = { ...prev };

        // Apply upgrade effects
        upgrades.forEach((upgrade) => {
          if (upgrade.id === "farm" && upgrade.level > 0) {
            newResources.food += (2 * upgrade.level) / 60; // per second
          }
          if (upgrade.id === "well" && upgrade.level > 0) {
            newResources.water += (1 * upgrade.level) / 60;
          }
          if (upgrade.id === "shelter" && upgrade.level > 0) {
            newResources.survivors += (1 * upgrade.level) / 3600; // 1 per hour
          }
        });

        // Survivor consumption
        newResources.food = Math.max(
          0,
          newResources.food - prev.survivors * 0.01,
        );
        newResources.water = Math.max(
          0,
          newResources.water - prev.survivors * 0.02,
        );

        // Floor and compare
        const floored = floorResources(newResources);
        if (!isEqualResources(floored, floorResources(prev))) {
          return floored;
        }
        return prev; // Skip update if unchanged
      });

      // Check for disasters
      if (Math.random() < DISASTER_CHANCE) {
        const disaster =
          disasters[Math.floor(Math.random() * disasters.length)];
        setShowDisaster(disaster);
        applyDisaster(disaster);
      }

      // Auto scavenging
      if (autoScavengeEnabled) {
        checkAutoScavenge();
      }
    }, 2000);

    return () => {
      if (gameLoopRef.current) clearInterval(gameLoopRef.current);
    };
  }, [upgrades, autoScavengeEnabled]);

  useEffect(() => {
    const interval = setInterval(() => {
      const now = Date.now();
      if (waitingForScavengePopup || scavengeResult) return;

      const finished = locations.find(
        (loc) =>
          loc.lastScavenged &&
          loc.scavengeTime > 0 &&
          now - loc.lastScavenged >= loc.scavengeTime,
      );

      if (finished) finalizeScavenge(finished);
    }, 1000);

    return () => clearInterval(interval);
  }, [locations, waitingForScavengePopup, scavengeResult]);

  const applyDisaster = (disaster: Disaster) => {
    setResources((prev) => ({
      food: Math.max(0, prev.food + (disaster.effect.food || 0)),
      water: Math.max(0, prev.water + (disaster.effect.water || 0)),
      materials: Math.max(0, prev.materials + (disaster.effect.materials || 0)),
      survivors: Math.max(1, prev.survivors + (disaster.effect.survivors || 0)),
    }));
  };

  const checkAutoScavenge = () => {
    if (waitingForScavengePopup) return;

    const now = Date.now();

    setLocations((prev) => {
      let hasActiveScavenge = false;
      let updated = false;

      const newLocations = prev.map((loc) => {
        if (loc.lastScavenged && loc.scavengeTime > 0) {
          if (now - loc.lastScavenged >= loc.scavengeTime && !updated) {
            const loot = {
              food: loc.resources.food || 0,
              water: loc.resources.water || 0,
              materials: loc.resources.materials || 0,
              survivors: loc.resources.survivors || 0,
            };

            finalizeScavenge(loc);
            updated = true;
            return loc;
          }

          hasActiveScavenge = true;
          return loc;
        }

        return loc;
      });

      if (!hasActiveScavenge && !updated) {
        // Start auto-scavenge on the first available location
        const targetIndex = newLocations.findIndex(
          (loc) =>
            loc.discovered &&
            !loc.lastScavenged &&
            loc.scavengeTime > 0 &&
            Object.values(loc.resources).some((v) => v && v > 0),
        );

        if (targetIndex !== -1) {
          newLocations[targetIndex] = {
            ...newLocations[targetIndex],
            lastScavenged: now,
          };
          updated = true;
        }
      }

      return newLocations;
    });
  };

  const finalizeScavenge = (location: MapLocation) => {
    const loot = {
      food: location.resources.food || 0,
      water: location.resources.water || 0,
      materials: location.resources.materials || 0,
      survivors: location.resources.survivors || 0,
    };

    setScavengeResult(loot);
    setResources((prevResources) => ({
      food: prevResources.food + loot.food,
      water: prevResources.water + loot.water,
      materials: prevResources.materials + loot.materials,
      survivors: prevResources.survivors + loot.survivors,
    }));
    setWaitingForScavengePopup(true);

    setLocations((prev) =>
      prev.map((loc) =>
        loc.id === location.id
          ? { ...loc, lastScavenged: null, resources: {}, wasScavenged: true }
          : loc,
      ),
    );
  };

  const handleMapClick = (
    e: React.MouseEvent<HTMLDivElement>,
    location: MapLocation,
  ) => {
    e.stopPropagation();
    if (!location.discovered && location.id !== "base") return;

    setSelectedLocation(location);
    setRadialMenuPosition({ x: e.clientX, y: e.clientY });
    setShowRadialMenu(true);
  };

  const scavengeLocation = (location: MapLocation) => {
    if (location.lastScavenged || location.scavengeTime === 0) return;

    setLocations((prev) =>
      prev.map((loc) =>
        loc.id === location.id ? { ...loc, lastScavenged: Date.now() } : loc,
      ),
    );
    setShowRadialMenu(false);
  };

  const exploreNearby = () => {
    if (!selectedLocation) return;

    const getGridCoords = (id: string) => {
      const match = id.match(/cell-(\d+)-(\d+)/);
      if (!match) return null;
      return { x: parseInt(match[1]), y: parseInt(match[2]) };
    };

    const selectedCoords = getGridCoords(selectedLocation.id);
    if (!selectedCoords) return;

    setLocations((prev) =>
      prev.map((loc) => {
        const coords = getGridCoords(loc.id);
        if (!coords || loc.discovered) return loc;

        const dx = Math.abs(coords.x - selectedCoords.x);
        const dy = Math.abs(coords.y - selectedCoords.y);

        if (dx <= 1 && dy <= 1) {
          return { ...loc, discovered: true };
        }

        return loc;
      }),
    );

    setShowRadialMenu(false);
  };

  const purchaseUpgrade = (upgrade: BaseUpgrade) => {
    if (upgrade.level >= upgrade.maxLevel) return;

    const canAfford = Object.entries(upgrade.cost).every(
      ([resource, cost]) =>
        resources[resource as keyof Resources] >= (cost || 0),
    );

    if (canAfford) {
      setResources((prev) => {
        const newResources = { ...prev };
        Object.entries(upgrade.cost).forEach(([resource, cost]) => {
          newResources[resource as keyof Resources] -= cost || 0;
        });
        return newResources;
      });

      setUpgrades((prev) =>
        prev.map((u) =>
          u.id === upgrade.id
            ? {
                ...u,
                level: u.level + 1,
                cost: Object.fromEntries(
                  Object.entries(u.cost).map(([r, c]) => [
                    r,
                    Math.floor((c || 0) * 1.5),
                  ]),
                ),
              }
            : u,
        ),
      );
    }
  };

  const getScavengeProgress = (location: MapLocation) => {
    if (!location.lastScavenged || location.scavengeTime === 0) return 0;
    const elapsed = Date.now() - location.lastScavenged;
    return Math.min(100, (elapsed / location.scavengeTime) * 100);
  };

  // Components
  const ResourcePanel = React.memo(
    ({
      resources,
      upgrades,
      autoScavengeEnabled,
      purchaseUpgrade,
      setAutoScavengeEnabled,
    }: {
      resources: Resources;
      upgrades: BaseUpgrade[];
      autoScavengeEnabled: boolean;
      purchaseUpgrade: (upgrade: BaseUpgrade) => void;
      setAutoScavengeEnabled: (enabled: boolean) => void;
    }) => {
      return (
        <div className="bg-gray-800 rounded-lg p-6 shadow-lg h-[calc(100%-60px)] flex flex-col">
          <div className="flex-none">
            <h2 className="text-2xl font-bold text-gray-100 mb-6">Resources</h2>
            <div className="grid grid-cols-2 gap-4 mb-8">
              <div className="bg-gray-700 rounded-lg p-4 shadow-md hover:shadow-lg transition-shadow">
                <div className="flex items-center justify-between">
                  <span className="text-gray-300">🍖 Food</span>
                  <span className="text-xl font-semibold text-green-400">
                    {Math.floor(resources.food)}
                  </span>
                </div>
              </div>
              <div className="bg-gray-700 rounded-lg p-4 shadow-md hover:shadow-lg transition-shadow">
                <div className="flex items-center justify-between">
                  <span className="text-gray-300">💧 Water</span>
                  <span className="text-xl font-semibold text-blue-400">
                    {Math.floor(resources.water)}
                  </span>
                </div>
              </div>
              <div className="bg-gray-700 rounded-lg p-4 shadow-md hover:shadow-lg transition-shadow">
                <div className="flex items-center justify-between">
                  <span className="text-gray-300">🔧 Materials</span>
                  <span className="text-xl font-semibold text-orange-400">
                    {Math.floor(resources.materials)}
                  </span>
                </div>
              </div>
              <div className="bg-gray-700 rounded-lg p-4 shadow-md hover:shadow-lg transition-shadow">
                <div className="flex items-center justify-between">
                  <span className="text-gray-300">👥 Survivors</span>
                  <span className="text-xl font-semibold text-purple-400">
                    {resources.survivors}
                  </span>
                </div>
              </div>
            </div>
            <h3 className="text-xl font-bold text-gray-100 mb-4">
              Base Upgrades
            </h3>
          </div>

          <div className="flex-grow overflow-y-auto md:pr-2">
            <div className="space-y-3">
              {upgrades.map((upgrade) => {
                const canAfford = Object.entries(upgrade.cost).every(
                  ([resource, cost]) =>
                    resources[resource as keyof Resources] >= (cost || 0),
                );

                return (
                  <div
                    key={upgrade.id}
                    className="bg-gray-700 rounded-lg p-3 shadow-md flex gap-4"
                  >
                    <div className="flex justify-between items-center mb-2 flex-grow border-r border-gray-600 md:pr-4">
                      <div>
                        <div className="flex items-center gap-2">
                          <h4 className="text-sm font-semibold text-gray-100">
                            {upgrade.name}
                          </h4>
                          <p className="text-xs text-green-400 mt-1">
                            {upgrade.effect}
                          </p>
                        </div>
                        <p className="text-xs text-gray-400">
                          {upgrade.description}
                        </p>
                      </div>
                      <div className="text-right">
                        <p className="text-sm text-gray-300">
                          Level {upgrade.level}/{upgrade.maxLevel}
                        </p>
                      </div>
                    </div>
                    {upgrade.level < upgrade.maxLevel && (
                      <button
                        onClick={() => purchaseUpgrade(upgrade)}
                        disabled={!canAfford}
                        className={`ml-auto mr-0 w-full max-w-20 sm:max-w-40 px-4 py-2 rounded-md transition-colors text-xs md:text-sm leading-4 ${
                          canAfford
                            ? "bg-green-600 hover:bg-green-700 text-white"
                            : "bg-gray-600 text-gray-400 cursor-not-allowed"
                        }`}
                      >
                        <p>Upgrade</p>
                        <p>
                          (
                          {Object.entries(upgrade.cost)
                            .map(([r, c]) => `${c} ${r}`)
                            .join(", ")}
                          )
                        </p>
                      </button>
                    )}
                  </div>
                );
              })}
            </div>
          </div>

          <div className="flex-none mt-4 pt-4 border-t border-gray-700">
            <label className="flex items-center space-x-2 text-gray-300">
              <input
                type="checkbox"
                checked={autoScavengeEnabled}
                onChange={(e) => setAutoScavengeEnabled(e.target.checked)}
                className="rounded"
              />
              <span>Auto-scavenge locations</span>
            </label>
          </div>
        </div>
      );
    },
  );

  const ScavengePopup = () => {
    if (!scavengeResult) return null;

    return (
      <div className="fixed inset-0 bg-black bg-opacity-60 flex items-center justify-center z-50">
        <div className="bg-gray-800 rounded-lg p-6 max-w-sm shadow-2xl">
          <h3 className="text-xl font-bold text-green-400 mb-2">
            Resources Found!
          </h3>
          <ul className="text-gray-200 space-y-1 mb-4">
            {Object.entries(scavengeResult).map(([key, value]) =>
              value > 0 ? (
                <li key={key}>
                  {key}:{" "}
                  <span className="font-semibold text-white">{value}</span>
                </li>
              ) : null,
            )}
          </ul>
          <button
            onClick={() => {
              setScavengeResult(null);
              setWaitingForScavengePopup(false); // Enables next scavenge
            }}
            className="w-full bg-green-700 hover:bg-green-800 text-white py-2 px-4 rounded transition-colors"
          >
            Continue
          </button>
        </div>
      </div>
    );
  };

  const MapView = () => (
    <div className="bg-gray-800 rounded-lg p-2 sm:p-6 shadow-lg h-full">
      <h2 className="text-2xl font-bold text-gray-100 mb-6">Wasteland Map</h2>
      <div
        className="grid bg-gray-900 rounded-lg aspect-square shadow-inner"
        style={{
          gridTemplateColumns: `repeat(${GRID_SIZE}, 1fr)`,
          gridTemplateRows: `repeat(${GRID_SIZE}, 1fr)`,
        }}
        onClick={() => setShowRadialMenu(false)}
      >
        {locations.map((location) => {
          return (
            <div
              key={location.id}
              className={`flex items-center justify-center ${
                location.discovered ? "cursor-pointer" : "opacity-50"
              }`}
              onClick={(e) => handleMapClick(e, location)}
            >
              {location.discovered ? (
                <div className="relative group">
                  <div
                    className={`w-16 h-16 rounded-sm flex items-center justify-center text-2xl shadow-lg transform transition-transform group-hover:scale-110 ${
                      location.id === "base" ? "bg-green-600" : "bg-blue-600"
                    }`}
                  >
                    {location.isBase
                      ? "🏠"
                      : location.wasScavenged
                      ? "✅"
                      : location.resources &&
                        Object.values(location.resources).some(
                          (val) => val && val > 0,
                        )
                      ? "💰"
                      : ""}
                  </div>
                  <div className="absolute -bottom-6 left-1/2 transform -translate-x-1/2 whitespace-nowrap">
                    <p className="text-xs text-gray-300">{location.name}</p>
                  </div>
                  {location.lastScavenged && location.scavengeTime > 0 && (
                    <div className="absolute bottom-1 left-2 right-2 h-1 bg-gray-700 rounded">
                      <div
                        className="h-full bg-green-500 rounded transition-all duration-1000"
                        style={{ width: `${getScavengeProgress(location)}%` }}
                      />
                    </div>
                  )}
                </div>
              ) : (
                <div className="w-16 h-16 rounded-sm bg-gray-700 flex items-center justify-center text-2xl opacity-35 animate-pulse"></div>
              )}
            </div>
          );
        })}
      </div>
    </div>
  );

  const RadialMenu = () => {
    if (!showRadialMenu || !selectedLocation) return null;

    const actions = [
      {
        label: "Scavenge",
        icon: "🔍",
        enabled:
          !selectedLocation.lastScavenged &&
          selectedLocation.scavengeTime > 0 &&
          Object.values(selectedLocation.resources).some(
            (val) => val && val > 0,
          ),
        action: () => scavengeLocation(selectedLocation),
      },
      {
        label: "Explore",
        icon: "🗺️",
        enabled: true,
        action: exploreNearby,
      },
      {
        label: "Cancel",
        icon: "❌",
        enabled: true,
        action: () => setShowRadialMenu(false),
      },
    ];

    return (
      <div
        className="fixed inset-0 z-50"
        onClick={() => setShowRadialMenu(false)}
      >
        <div
          className="absolute"
          style={{ left: radialMenuPosition.x, top: radialMenuPosition.y }}
        >
          {actions.map((action, index) => {
            const angle = (index * 120 - 60) * (Math.PI / 180);
            const x = Math.cos(angle) * 80;
            const y = Math.sin(angle) * 80;

            return (
              <button
                key={action.label}
                disabled={!action.enabled}
                onClick={(e) => {
                  e.stopPropagation();
                  action.action();
                }}
                className={`absolute transform -translate-x-1/2 -translate-y-1/2 w-16 h-16 rounded-full flex flex-col items-center justify-center text-white transition-all ${
                  action.enabled
                    ? "bg-blue-600 hover:bg-blue-700 shadow-lg hover:shadow-xl"
                    : "bg-gray-600 cursor-not-allowed opacity-50"
                }`}
                style={{ left: x, top: y }}
              >
                <span className="text-xl">{action.icon}</span>
                <span className="text-xs mt-1">{action.label}</span>
              </button>
            );
          })}
        </div>
      </div>
    );
  };

  const DisasterPopup = () => {
    if (!showDisaster) return null;

    return (
      <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
        <div className="bg-red-900 rounded-lg p-6 max-w-sm shadow-2xl animate-pulse">
          <h3 className="text-2xl font-bold text-white mb-2">
            {showDisaster.name}
          </h3>
          <p className="text-gray-200 mb-4">{showDisaster.description}</p>
          <div className="text-sm text-gray-300 mb-4">
            {Object.entries(showDisaster.effect).map(([resource, amount]) => (
              <p key={resource}>
                {resource}: {amount}
              </p>
            ))}
          </div>
          <button
            onClick={() => setShowDisaster(null)}
            className="w-full bg-red-700 hover:bg-red-800 text-white py-2 px-4 rounded transition-colors"
          >
            Continue
          </button>
        </div>
      </div>
    );
  };

  return (
    <div className="min-h-screen bg-gray-900 text-white">
      {isMobile ? (
        // Mobile layout with tabs
        <div className="px-2 py-4">
          <h1 className="text-3xl font-bold text-center mb-6">
            Wasteland Survivor
          </h1>
          <div className="flex mb-4 bg-gray-800 rounded-lg p-1">
            <button
              onClick={() => setActiveTab("resources")}
              className={`flex-1 py-2 px-4 rounded-md transition-colors ${
                activeTab === "resources"
                  ? "bg-gray-700 text-white"
                  : "text-gray-400 hover:text-white"
              }`}
            >
              Resources
            </button>
            <button
              onClick={() => setActiveTab("map")}
              className={`flex-1 py-2 px-4 rounded-md transition-colors ${
                activeTab === "map"
                  ? "bg-gray-700 text-white"
                  : "text-gray-400 hover:text-white"
              }`}
            >
              Map
            </button>
          </div>
          {activeTab === "resources" ? (
            <ResourcePanel
              resources={resources}
              upgrades={upgrades}
              autoScavengeEnabled={autoScavengeEnabled}
              purchaseUpgrade={purchaseUpgrade}
              setAutoScavengeEnabled={setAutoScavengeEnabled}
            />
          ) : (
            <MapView />
          )}
        </div>
      ) : (
        // Desktop split-screen layout
        <div className="flex h-screen">
          <div className="w-1/2 p-6 overflow-y-auto">
            <h1 className="text-3xl font-bold mb-6">Wasteland Survivor</h1>
            <ResourcePanel
              resources={resources}
              upgrades={upgrades}
              autoScavengeEnabled={autoScavengeEnabled}
              purchaseUpgrade={purchaseUpgrade}
              setAutoScavengeEnabled={setAutoScavengeEnabled}
            />
          </div>
          <div className="w-1/2 p-6 border-l border-gray-700 flex flex-col h-full">
            <MapView />
          </div>
        </div>
      )}

      <RadialMenu />
      <DisasterPopup />
      <ScavengePopup />
    </div>
  );
};

export default ApocalypticGame;
